import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Activity, Flame, Footprints, Dumbbell, Moon, Droplets, Scale, 
  Plus, Calendar, MoreHorizontal 
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  
  // Get Current Date (e.g., "Monday, Jan 15")
  const todayDate = new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' });

  // State
  const [stats, setStats] = useState({ 
    calories: 0, steps: 0, workouts: 0, activeMinutes: 0, weight: 0 
  });
  const [goals, setGoals] = useState([]);

  // Fetch Data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const headers = { 'Authorization': `Bearer ${user.token}` };
        
        const [workoutsRes, nutritionRes, statsRes, goalsRes] = await Promise.all([
          fetch('http://localhost:5000/api/workouts', { headers }),
          fetch('http://localhost:5000/api/nutrition', { headers }),
          fetch('http://localhost:5000/api/stats', { headers }),
          fetch('http://localhost:5000/api/goals', { headers })
        ]);

        const workoutsData = await workoutsRes.json();
        const nutritionData = await nutritionRes.json();
        const statsData = await statsRes.json();
        const goalsData = await goalsRes.json();

        // Calculate Totals for Today
        const today = new Date().toDateString();
        let cal = 0, prot = 0, carb = 0, fat = 0;
        
        nutritionData.forEach(item => { 
          if (new Date(item.date).toDateString() === today) {
            cal += item.calories || 0;
            prot += item.protein || 0;
            carb += item.carbs || 0;
            fat += item.fat || 0;
          }
        });

        let mins = 0, count = 0;
        workoutsData.forEach(item => { 
          if (new Date(item.date).toDateString() === today) { 
            count += 1; 
            mins += item.duration || 0; 
          } 
        });

        const latestStat = statsData[0] || {}; 
        
        setStats({ 
          calories: cal, 
          protein: prot,
          carbs: carb,
          fat: fat,
          steps: latestStat.steps || 0, 
          workouts: count, 
          activeMinutes: mins,
          weight: latestStat.weight || 0
        });

        setGoals(goalsData.filter(g => g.status === 'active'));

      } catch (error) { console.error(error); }
    };

    if (user) fetchData();
  }, [user]);

  // Reusable Detail Card Component (Matches your screenshot design)
  const MetricCard = ({ title, value, subtext, icon: Icon, color, progress, details, footer }) => (
    <div className="bg-[#121212] border border-gray-800 p-6 rounded-2xl relative">
      <div className="flex justify-between items-start mb-4">
        <div className="flex items-center gap-3">
          <div className={`p-2 rounded-lg ${color} bg-opacity-10 text-white`}>
            <Icon size={20} className={color.replace('bg-', 'text-')} />
          </div>
          <h3 className="font-bold text-white">{title}</h3>
        </div>
        <MoreHorizontal size={16} className="text-gray-500 cursor-pointer" />
      </div>

      <div className="mb-4">
        <h2 className="text-3xl font-bold text-white mb-1">{value}</h2>
        <p className="text-xs text-gray-500">{subtext}</p>
      </div>

      {/* Progress Bar */}
      <div className="h-2 w-full bg-gray-800 rounded-full mb-4 overflow-hidden">
        <div className={`h-full ${color}`} style={{ width: `${progress}%` }}></div>
      </div>

      {/* Footer / Details */}
      {details && (
        <div className="flex justify-between text-xs text-gray-400 mt-2">
          {details.map((d, i) => (
            <div key={i}>
              <span className="block text-white font-bold">{d.val}</span>
              <span>{d.label}</span>
            </div>
          ))}
        </div>
      )}
      
      {footer && <p className="text-xs text-green-400 mt-2 flex items-center gap-1">{footer}</p>}
    </div>
  );

  return (
    <div className="min-h-screen bg-[#0A0A0A] p-6 pb-24 text-white">
      
      {/* 1. Header Section */}
      <div className="flex justify-between items-start mb-8">
        <div>
          <h1 className="text-3xl font-bold text-white">Welcome back, {user?.name}!</h1>
          <p className="text-gray-400">Let's check your fitness progress today</p>
        </div>
        <div className="text-right">
          <button className="bg-[#00B4D8] text-white font-bold px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 mb-2">
            <Plus size={18} /> Quick Log
          </button>
          <p className="text-xs text-gray-500 flex items-center justify-end gap-1">
             <Calendar size={12} /> {todayDate}
          </p>
        </div>
      </div>

      {/* 2. Top Summary Row (4 Cards) */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <div className="bg-[#1e293b] p-4 rounded-xl flex flex-col justify-center border-l-4 border-blue-500">
           <div className="flex items-center gap-2 mb-1"><Flame size={16} className="text-blue-400"/> <span className="text-xs text-blue-200">Calories</span></div>
           <span className="text-xl font-bold">{stats.calories} / 2,000</span>
        </div>
        <div className="bg-[#064e3b] p-4 rounded-xl flex flex-col justify-center border-l-4 border-green-500">
           <div className="flex items-center gap-2 mb-1"><Footprints size={16} className="text-green-400"/> <span className="text-xs text-green-200">Steps</span></div>
           <span className="text-xl font-bold">{stats.steps} / 10,000</span>
        </div>
        <div className="bg-[#4c1d95] p-4 rounded-xl flex flex-col justify-center border-l-4 border-purple-500">
           <div className="flex items-center gap-2 mb-1"><Activity size={16} className="text-purple-400"/> <span className="text-xs text-purple-200">Active Mins</span></div>
           <span className="text-xl font-bold">{stats.activeMinutes} / 60</span>
        </div>
        <div className="bg-[#1e1b4b] p-4 rounded-xl flex flex-col justify-center border-l-4 border-indigo-500">
           <div className="flex items-center gap-2 mb-1"><Moon size={16} className="text-indigo-400"/> <span className="text-xs text-indigo-200">Sleep</span></div>
           <span className="text-xl font-bold">7h 15m</span>
        </div>
      </div>

      <h3 className="text-lg font-bold mb-4">Your Fitness Metrics</h3>

      {/* 3. Main Grid (6 Cards Layout) */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        
        {/* Row 1 */}
        <MetricCard 
          title="Steps" 
          value={stats.steps.toLocaleString()} 
          subtext="Goal: 10,000 steps" 
          icon={Footprints} 
          color="bg-green-500" 
          progress={(stats.steps/10000)*100}
          footer="↑ 12% more than yesterday"
        />
        
        <MetricCard 
          title="Workouts" 
          value={`${stats.workouts}/2`} 
          subtext="Goal: 2 workouts today" 
          icon={Dumbbell} 
          color="bg-pink-500" 
          progress={(stats.workouts/2)*100}
          footer="Upper Body • 45 mins • 320 cal"
        />
        
        <MetricCard 
          title="Nutrition" 
          value={`${stats.calories} cal`} 
          subtext="Goal: 2,000 calories" 
          icon={Flame} 
          color="bg-blue-400" 
          progress={(stats.calories/2000)*100}
          details={[
            {val: `${stats.protein || 0}g`, label: 'Protein'},
            {val: `${stats.carbs || 0}g`, label: 'Carbs'},
            {val: `${stats.fat || 0}g`, label: 'Fat'}
          ]}
        />

        {/* Row 2 */}
        <MetricCard 
          title="Sleep" 
          value="7h 15m" 
          subtext="Goal: 8 hours" 
          icon={Moon} 
          color="bg-indigo-500" 
          progress={90}
          footer="11:30 PM - 6:45 AM • Deep sleep: 2h 10m"
        />

        <MetricCard 
          title="Weight" 
          value={`${stats.weight} kg`} 
          subtext="Goal: 70 kg" 
          icon={Scale} 
          color="bg-yellow-500" 
          progress={85}
          footer="↓ 2.5 kg this month"
        />

        <MetricCard 
          title="Water Intake" 
          value="5 / 8" 
          subtext="Goal: 8 glasses" 
          icon={Droplets} 
          color="bg-cyan-400" 
          progress={(5/8)*100}
          footer="Last glass: 2:45 PM"
        />

      </div>
    </div>
  );
};

export default Dashboard;
