import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { X, Flame, Search, Calculator } from 'lucide-react';

const MealModel = ({ isOpen, onClose, onSave }) => {
  const { user } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  
  // Search State
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [showResults, setShowResults] = useState(false);

  // Logic State
  const [selectedFood, setSelectedFood] = useState(null); // Stores base values (per 1 unit)
  const [quantity, setQuantity] = useState(1); // Default to 1

  // Final Form Data (Sent to Backend)
  const [formData, setFormData] = useState({ 
    mealType: 'Breakfast', foodName: '', calories: '', protein: '', carbs: '', fat: '' 
  });

  // Search API
  useEffect(() => {
    const searchFoods = async () => {
      if (query.length < 2) { setResults([]); return; }
      try {
        const res = await fetch(`http://localhost:5000/api/foods/search?query=${query}`);
        const data = await res.json();
        setResults(data);
        setShowResults(true);
      } catch (err) { console.error(err); }
    };
    const delayDebounceFn = setTimeout(() => searchFoods(), 300);
    return () => clearTimeout(delayDebounceFn);
  }, [query]);

  // 1. SELECT FOOD -> Store Base Values & Set Initial Form Data
  const handleSelectFood = (food) => {
    setSelectedFood(food); // Remember "1 Egg = 6g Protein"
    setQuantity(1);        // Reset quantity
    setQuery('');          // Clear search bar
    setShowResults(false);

    // Auto-fill form with base values
    setFormData({
      ...formData,
      foodName: food.name,
      calories: food.calories,
      protein: food.protein,
      carbs: food.carbs,
      fat: food.fat
    });
  };

  // 2. CHANGE QUANTITY -> Multiply Base Values
  const handleQuantityChange = (e) => {
    const qty = parseFloat(e.target.value) || 0;
    setQuantity(qty);

    if (selectedFood) {
      setFormData(prev => ({
        ...prev,
        calories: (selectedFood.calories * qty).toFixed(0),
        protein: (selectedFood.protein * qty).toFixed(1),
        carbs: (selectedFood.carbs * qty).toFixed(1),
        fat: (selectedFood.fat * qty).toFixed(1)
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await fetch('http://localhost:5000/api/nutrition', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${user.token}` },
        body: JSON.stringify(formData)
      });
      onSave(); 
      onClose();
      // Reset
      setFormData({ mealType: 'Breakfast', foodName: '', calories: '', protein: '', carbs: '', fat: '' });
      setSelectedFood(null);
      setQuantity(1);
    } catch (error) { console.error(error); } finally { setLoading(false); }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center z-[60] p-4">
      <div className="bg-[#121212] border border-gray-800 w-full max-w-lg rounded-2xl p-6 shadow-2xl relative">
        
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-bold text-white flex items-center gap-2">
            <Flame className="text-[#D4FF33]" size={24} /> Log Meal
          </h2>
          <button onClick={onClose} className="text-gray-400 hover:text-white"><X size={24} /></button>
        </div>

        {/* 1. SEARCH BAR */}
        <div className="relative mb-6">
          <div className="absolute left-3 top-3 text-gray-500"><Search size={18} /></div>
          <input 
            type="text" 
            placeholder="Search food (e.g. 'Egg', 'Rice')" 
            className="w-full bg-[#0A0A0A] border border-gray-700 rounded-xl p-3 pl-10 text-white focus:border-[#D4FF33] focus:outline-none placeholder-gray-600"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          
          {/* Dropdown Results */}
          {showResults && results.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-[#1e1e1e] border border-gray-700 rounded-xl shadow-2xl z-50 max-h-48 overflow-y-auto">
              {results.map((food) => (
                <div 
                  key={food._id}
                  onClick={() => handleSelectFood(food)}
                  className="p-3 hover:bg-[#D4FF33] hover:text-black cursor-pointer border-b border-gray-800 last:border-0 transition-colors flex justify-between items-center"
                >
                  <div>
                    <div className="font-bold text-sm">{food.name}</div>
                    <div className="text-[10px] opacity-70">
                      {food.servingSize} â€¢ {food.calories} cal
                    </div>
                  </div>
                  <div className="text-[10px] font-mono opacity-50">
                    P:{food.protein} C:{food.carbs} F:{food.fat}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <form onSubmit={handleSubmit} className="space-y-5">
          
          {/* Meal Type Buttons */}
          <div>
            <label className="text-xs text-gray-500 mb-2 block uppercase font-bold tracking-wider">Meal Type</label>
            <div className="flex gap-2">
              {['Breakfast', 'Lunch', 'Dinner', 'Snack'].map(type => (
                <button
                  type="button"
                  key={type}
                  onClick={() => setFormData({...formData, mealType: type})}
                  className={`flex-1 py-2 text-xs rounded-lg border transition-all ${
                    formData.mealType === type 
                      ? 'bg-[#D4FF33] text-black border-[#D4FF33] font-bold shadow-[0_0_10px_rgba(212,255,51,0.3)]' 
                      : 'bg-transparent text-gray-400 border-gray-800 hover:border-gray-600'
                  }`}
                >
                  {type}
                </button>
              ))}
            </div>
          </div>

          {/* 2. QUANTITY ADJUSTER (Only shows if a food is selected) */}
          {selectedFood && (
            <div className="bg-[#1a1a1a] p-3 rounded-xl border border-gray-800 flex items-center justify-between">
              <div>
                <span className="text-gray-400 text-xs block mb-1">Serving Size: <span className="text-[#D4FF33]">{selectedFood.servingSize}</span></span>
                <div className="font-bold text-white text-sm">{selectedFood.name}</div>
              </div>
              <div className="flex items-center gap-2 bg-black/30 p-1 rounded-lg border border-gray-700">
                <span className="text-xs text-gray-500 pl-2">Qty:</span>
                <input 
                  type="number" 
                  min="0.1" 
                  step="0.1"
                  className="w-16 bg-transparent text-white font-bold text-center focus:outline-none"
                  value={quantity}
                  onChange={handleQuantityChange}
                />
              </div>
            </div>
          )}

          {/* Food Name (Manual Edit Allowed) */}
          {!selectedFood && (
             <div>
               <label className="text-xs text-gray-500 mb-1 block">Food Name</label>
               <input type="text" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.foodName} onChange={(e) => setFormData({...formData, foodName: e.target.value})} required />
             </div>
          )}

          {/* Macros Grid (Auto-calculated but editable) */}
          <div className="grid grid-cols-2 gap-4">
            <div className="relative">
              <label className="text-xs text-gray-500 mb-1 block">Calories</label>
              <input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none font-bold" value={formData.calories} onChange={(e) => setFormData({...formData, calories: e.target.value})} required />
            </div>
            <div className="relative">
              <label className="text-xs text-blue-400 mb-1 block">Protein (g)</label>
              <input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-blue-500 focus:outline-none" value={formData.protein} onChange={(e) => setFormData({...formData, protein: e.target.value})} />
            </div>
            <div className="relative">
              <label className="text-xs text-yellow-500 mb-1 block">Carbs (g)</label>
              <input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-yellow-500 focus:outline-none" value={formData.carbs} onChange={(e) => setFormData({...formData, carbs: e.target.value})} />
            </div>
            <div className="relative">
              <label className="text-xs text-red-500 mb-1 block">Fat (g)</label>
              <input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-red-500 focus:outline-none" value={formData.fat} onChange={(e) => setFormData({...formData, fat: e.target.value})} />
            </div>
          </div>

          <button disabled={loading} type="submit" className="w-full bg-[#D4FF33] text-black font-bold py-3.5 rounded-xl mt-4 hover:opacity-90 transition shadow-lg shadow-[#D4FF33]/10">
            {loading ? 'Saving...' : 'Save Meal Entry'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default MealModel;
