import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';
import BottomNav from './components/BottomNav';
import Dashboard from './pages/Dashboard';
import Workouts from './pages/Workouts';
import AICoach from './pages/AICoach';
import Nutrition from './pages/Nutrition';
import Login from './pages/Login';
import Register from './pages/Register';
import Onboarding from './pages/Onboarding';

// Protected Route Wrapper
const PrivateRoute = ({ children }) => {
  const { user, loading } = useContext(AuthContext);
  if (loading) return <div>Loading...</div>;
  return user ? children : <Navigate to="/login" />;
};

const Placeholder = ({ title }) => (
  <div className="p-6 text-white min-h-screen pb-20">
    <h1 className="text-3xl font-bold mb-4">{title}</h1>
    <p className="text-gray-400">Feature coming soon.</p>
  </div>
);

function App() {
  const { user } = useContext(AuthContext);

  return (
    <Router>
      <div className="bg-background min-h-screen text-white font-sans">
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Routes */}
          <Route path="/onboarding" element={<PrivateRoute><Onboarding /></PrivateRoute>} />

          <Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />
          <Route path="/workouts" element={<PrivateRoute><Workouts /></PrivateRoute>} />
          <Route path="/ai-coach" element={<PrivateRoute><AI Coach /></PrivateRoute>} />
          <Route path="/nutrition" element={<PrivateRoute><Nutrition/></PrivateRoute>} />
          <Route path="/profile" element={<PrivateRoute><Placeholder title="Profile" /></PrivateRoute>} />

          {/* Default Redirect */}
          <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} />
        </Routes>

        {/* Show Bottom Nav only if logged in */}
        {user && <BottomNav />}
      </div>
    </Router>
  );
}

export default App;
