import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ThemeContext } from '../context/ThemeContext';
import { 
  User, Mail, Calendar, Dumbbell, Flame, Trophy, 
  Save, Sun, Moon, Eye 
} from 'lucide-react';

const Profile = () => {
  const { user, logout } = useContext(AuthContext);
  const { theme, changeTheme, styles } = useContext(ThemeContext);
  
  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    age: '',
    height: '',
    fitnessGoal: '',
    joinDate: ''
  });

  const [stats, setStats] = useState({ workouts: 0, calories: 0, achievements: 0 });
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState('');

  // 1. Fetch User Data & Stats
  useEffect(() => {
    const fetchData = async () => {
      if (!user?.token) return;
      const headers = { 'Authorization': `Bearer ${user.token}` };

      try {
        // Get Profile
        const profileRes = await fetch('http://localhost:5000/api/users/profile', { headers });
        const profile = await profileRes.json();

        // Get Workouts (for stats)
        const workoutsRes = await fetch('http://localhost:5000/api/workouts', { headers });
        const workouts = await workoutsRes.json();
        
        // Calculate Totals
        const totalCals = workouts.reduce((acc, curr) => acc + (curr.caloriesBurned || 0), 0);

        setProfileData({
          name: profile.name,
          email: profile.email,
          age: profile.age || '',
          height: profile.height || '',
          fitnessGoal: profile.fitnessGoal || 'General Health',
          joinDate: new Date(profile.createdAt).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
        });

        setStats({
          workouts: workouts.length,
          calories: totalCals,
          achievements: profile.achievements?.length || 0
        });

      } catch (error) {
        console.error("Error fetching profile", error);
      }
    };
    fetchData();
  }, [user]);

  // 2. Handle Update
  const handleUpdate = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg('');

    try {
      const response = await fetch('http://localhost:5000/api/users/profile', {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({
          name: profileData.name,
          age: profileData.age,
          height: profileData.height,
          fitnessGoal: profileData.fitnessGoal
        }),
      });

      if (response.ok) {
        setMsg('Profile updated successfully!');
        // Update local user context if needed (optional)
      } else {
        setMsg('Failed to update.');
      }
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen p-6 pb-24 ${styles.bg} ${styles.text}`}>
      
      {/* Header */}
      <div className="flex justify-between items-start mb-8">
        <div>
          <h1 className="text-3xl font-bold">Profile</h1>
          <p className={styles.subtext}>Manage your account and preferences</p>
        </div>
        <button 
          onClick={logout}
          className="text-red-500 text-sm font-bold border border-red-500/30 px-4 py-2 rounded-lg hover:bg-red-500/10 transition"
        >
          Sign Out
        </button>
      </div>

      {/* Profile Header Card */}
      <div className={`${styles.card} border ${styles.border} p-6 rounded-2xl mb-6 flex flex-col md:flex-row items-center gap-6`}>
        <div className="h-20 w-20 bg-primary rounded-full flex items-center justify-center text-3xl font-bold text-black">
          {profileData.name.charAt(0).toUpperCase()}
        </div>
        <div className="flex-1 text-center md:text-left">
          <h2 className="text-2xl font-bold">{profileData.name}</h2>
          <p className={styles.subtext}>{profileData.email}</p>
          <div className="flex items-center justify-center md:justify-start gap-2 mt-2 text-xs font-medium bg-primary/10 text-primary w-fit mx-auto md:mx-0 px-3 py-1 rounded-full">
            <Calendar size={12} /> Member since {profileData.joinDate}
          </div>
        </div>
        
        {/* Quick Stats Row within Card */}
        <div className="flex gap-6 border-t md:border-t-0 md:border-l border-gray-700 pt-4 md:pt-0 md:pl-6">
          <div className="text-center">
            <div className="text-primary font-bold text-xl flex justify-center items-center gap-1">
              <Dumbbell size={16}/> {stats.workouts}
            </div>
            <p className="text-xs text-gray-500 uppercase">Workouts</p>
          </div>
          <div className="text-center">
            <div className="text-orange-500 font-bold text-xl flex justify-center items-center gap-1">
              <Flame size={16}/> {stats.calories}
            </div>
            <p className="text-xs text-gray-500 uppercase">Calories</p>
          </div>
          <div className="text-center">
            <div className="text-yellow-500 font-bold text-xl flex justify-center items-center gap-1">
              <Trophy size={16}/> {stats.achievements}
            </div>
            <p className="text-xs text-gray-500 uppercase">Badges</p>
          </div>
        </div>
      </div>

      {/* --- THEME SETTINGS --- */}
      <div className="mb-8">
        <h3 className="text-lg font-bold mb-4">App Appearance</h3>
        <div className="grid grid-cols-3 gap-4">
          <button 
            onClick={() => changeTheme('dark')}
            className={`p-4 rounded-xl border flex flex-col items-center gap-2 transition ${
              theme === 'dark' ? 'border-primary bg-primary/10' : `${styles.card} ${styles.border}`
            }`}
          >
            <Moon size={24} className={theme === 'dark' ? 'text-primary' : 'text-gray-400'} />
            <span className="text-xs font-bold">Dark Mode</span>
          </button>

          <button 
            onClick={() => changeTheme('light')}
            className={`p-4 rounded-xl border flex flex-col items-center gap-2 transition ${
              theme === 'light' ? 'border-primary bg-primary/10' : `${styles.card} ${styles.border}`
            }`}
          >
            <Sun size={24} className={theme === 'light' ? 'text-primary' : 'text-gray-400'} />
            <span className="text-xs font-bold">Light Mode</span>
          </button>

          <button 
            onClick={() => changeTheme('orange')}
            className={`p-4 rounded-xl border flex flex-col items-center gap-2 transition ${
              theme === 'orange' ? 'border-orange-500 bg-orange-500/10' : `${styles.card} ${styles.border}`
            }`}
          >
            <Eye size={24} className={theme === 'orange' ? 'text-orange-500' : 'text-gray-400'} />
            <span className="text-xs font-bold">Eye Care</span>
          </button>
        </div>
      </div>

      {/* Edit Form */}
      <form onSubmit={handleUpdate} className={`${styles.card} border ${styles.border} p-6 rounded-2xl`}>
        <h3 className="text-lg font-bold mb-6 flex items-center gap-2">
          <User size={20} /> Personal Details
        </h3>

        {msg && <p className="mb-4 text-green-500 text-sm">{msg}</p>}

        <div className="space-y-4">
          <div>
            <label className={`block text-sm mb-1 ${styles.subtext}`}>Full Name</label>
            <input 
              type="text" 
              className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text} focus:border-primary focus:outline-none`}
              value={profileData.name}
              onChange={(e) => setProfileData({...profileData, name: e.target.value})}
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className={`block text-sm mb-1 ${styles.subtext}`}>Age</label>
              <input 
                type="number" 
                className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text} focus:border-primary focus:outline-none`}
                value={profileData.age}
                onChange={(e) => setProfileData({...profileData, age: e.target.value})}
              />
            </div>
            <div>
              <label className={`block text-sm mb-1 ${styles.subtext}`}>Height (cm)</label>
              <input 
                type="number" 
                className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text} focus:border-primary focus:outline-none`}
                value={profileData.height}
                onChange={(e) => setProfileData({...profileData, height: e.target.value})}
              />
            </div>
          </div>

          <div>
            <label className={`block text-sm mb-1 ${styles.subtext}`}>Fitness Goal</label>
            <select 
               className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text} focus:border-primary focus:outline-none`}
               value={profileData.fitnessGoal}
               onChange={(e) => setProfileData({...profileData, fitnessGoal: e.target.value})}
            >
              <option>General Health</option>
              <option>Weight Loss</option>
              <option>Muscle Gain</option>
              <option>Endurance</option>
              <option>Flexibility</option>
            </select>
          </div>
        </div>

        <button 
          type="submit" 
          disabled={loading}
          className="w-full bg-primary text-black font-bold py-3 rounded-xl mt-6 hover:opacity-90 flex justify-center items-center gap-2"
        >
          {loading ? 'Saving...' : <><Save size={18}/> Save Changes</>}
        </button>
      </form>
      
    </div>
  );
};

export default Profile;
