import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Ruler, Weight, User, Target, ChevronRight } from 'lucide-react';

const Onboarding = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);

  const [data, setData] = useState({
    age: '',
    gender: 'Male',
    height: '',
    weight: '',
    fitnessGoal: 'General Health'
  });

  const handleNext = async () => {
    if (step < 3) {
      setStep(step + 1);
    } else {
      // Final Submit
      setLoading(true);
      try {
        await fetch('http://localhost:5000/api/users/profile', {
          method: 'PUT',
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`
          },
          body: JSON.stringify(data)
        });
        navigate('/dashboard');
      } catch (error) {
        console.error(error);
        setLoading(false);
      }
    }
  };

  return (
    <div className="min-h-screen bg-[#0A0A0A] text-white flex items-center justify-center p-6 relative overflow-hidden">
      
      {/* Background Ambience */}
      <div className="absolute top-0 left-0 w-full h-full bg-[url('https://images.unsplash.com/photo-1599058945522-28d584b6f0ff?q=80&w=1469&auto=format&fit=crop')] bg-cover opacity-10 pointer-events-none"></div>

      <div className="w-full max-w-lg bg-[#121212] border border-gray-800 p-8 rounded-3xl relative z-10 shadow-2xl">
        
        {/* Progress Bar */}
        <div className="flex gap-2 mb-8">
          <div className={`h-1.5 flex-1 rounded-full ${step >= 1 ? 'bg-[#D4FF33]' : 'bg-gray-800'}`}></div>
          <div className={`h-1.5 flex-1 rounded-full ${step >= 2 ? 'bg-[#D4FF33]' : 'bg-gray-800'}`}></div>
          <div className={`h-1.5 flex-1 rounded-full ${step >= 3 ? 'bg-[#D4FF33]' : 'bg-gray-800'}`}></div>
        </div>

        <h1 className="text-3xl font-bold mb-2">Let's get to know you</h1>
        <p className="text-gray-400 mb-8">This helps us personalize your experience.</p>

        {/* STEP 1: Basic Info */}
        {step === 1 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-right-4">
             <div>
                <label className="text-xs text-gray-500 uppercase font-bold block mb-2">Gender</label>
                <div className="flex gap-4">
                  {['Male', 'Female', 'Other'].map(g => (
                    <button 
                      key={g}
                      onClick={() => setData({...data, gender: g})}
                      className={`flex-1 py-3 rounded-xl border transition-all ${
                        data.gender === g 
                        ? 'bg-[#D4FF33] text-black border-[#D4FF33] font-bold' 
                        : 'bg-[#1e1e1e] border-gray-700 text-gray-400 hover:border-gray-500'
                      }`}
                    >
                      {g}
                    </button>
                  ))}
                </div>
             </div>
             <div>
                <label className="text-xs text-gray-500 uppercase font-bold block mb-2">Age</label>
                <input 
                  type="number" 
                  className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-4 text-white focus:border-[#D4FF33] focus:outline-none"
                  placeholder="e.g. 24"
                  value={data.age}
                  onChange={e => setData({...data, age: e.target.value})}
                />
             </div>
          </div>
        )}

        {/* STEP 2: Measurements */}
        {step === 2 && (
          <div className="space-y-4 animate-in fade-in slide-in-from-right-4">
             <div>
                <label className="text-xs text-gray-500 uppercase font-bold block mb-2">Height (cm)</label>
                <div className="relative">
                  <Ruler className="absolute left-4 top-4 text-gray-500" />
                  <input 
                    type="number" 
                    className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-4 pl-12 text-white focus:border-[#D4FF33] focus:outline-none"
                    placeholder="e.g. 175"
                    value={data.height}
                    onChange={e => setData({...data, height: e.target.value})}
                  />
                </div>
             </div>
             <div>
                <label className="text-xs text-gray-500 uppercase font-bold block mb-2">Weight (kg)</label>
                <div className="relative">
                  <Weight className="absolute left-4 top-4 text-gray-500" />
                  <input 
                    type="number" 
                    className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-4 pl-12 text-white focus:border-[#D4FF33] focus:outline-none"
                    placeholder="e.g. 70"
                    value={data.weight}
                    onChange={e => setData({...data, weight: e.target.value})}
                  />
                </div>
             </div>
          </div>
        )}

        {/* STEP 3: Goal */}
        {step === 3 && (
          <div className="space-y-3 animate-in fade-in slide-in-from-right-4">
             <label className="text-xs text-gray-500 uppercase font-bold block mb-2">Primary Goal</label>
             {['Weight Loss', 'Muscle Gain', 'General Health', 'Endurance'].map(goal => (
                <button 
                  key={goal}
                  onClick={() => setData({...data, fitnessGoal: goal})}
                  className={`w-full py-4 px-6 rounded-xl border text-left flex justify-between items-center transition-all ${
                    data.fitnessGoal === goal 
                    ? 'bg-[#D4FF33] text-black border-[#D4FF33] font-bold' 
                    : 'bg-[#1e1e1e] border-gray-700 text-gray-400 hover:border-gray-500'
                  }`}
                >
                  {goal}
                  {data.fitnessGoal === goal && <Target size={20} />}
                </button>
             ))}
          </div>
        )}

        {/* Action Button */}
        <button 
          onClick={handleNext}
          disabled={loading}
          className="w-full bg-[#D4FF33] text-black font-bold py-4 rounded-xl mt-8 hover:opacity-90 transition flex items-center justify-center gap-2"
        >
          {loading ? 'Setting up Profile...' : (step === 3 ? 'Finish Setup' : 'Continue')}
          {!loading && <ChevronRight size={20} />}
        </button>

      </div>
    </div>
  );
};

export default Onboarding;
