import React, { useState, useContext, useRef, useEffect } from 'react';
import { Send, Bot, Sparkles, Activity, AlertCircle } from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

const AICoach = () => {
  const { user } = useContext(AuthContext);
  const [messages, setMessages] = useState([
    { 
      role: 'ai', 
      content: `Hello ${user?.name || 'Champ'}! üëã\nI am your **AI Coach**. I know your goal is **${user?.fitnessGoal || 'to stay fit'}**.\n\nHow can I help you train today?` 
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to latest message
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const handleSend = async (text = input) => {
    if (!text.trim()) return;

    const userMsg = { role: 'user', content: text };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ message: text })
      });
      
      const data = await response.json();

      if (!response.ok) throw new Error(data.response || "Server Error");

      const aiMsg = { role: 'ai', content: data.response };
      setMessages(prev => [...prev, aiMsg]);

    } catch (error) {
      console.error("Chat Error:", error);
      setMessages(prev => [...prev, { 
        role: 'ai', 
        content: "‚ö†Ô∏è **Connection Issue:** I'm having trouble reaching my AI brain. Try asking for a 'Chest' or 'Back' workout‚Äîthose are stored in my local memory!" 
      }]);
    } finally {
      setLoading(false);
    }
  };

  // Improved Formatter for AI Responses (Bold & Lists)
  const FormatMessage = ({ content }) => {
    return content.split('\n').map((line, i) => {
      // Detect list items for special styling
      const isListItem = line.trim().startsWith('1.') || line.trim().startsWith('-') || line.trim().startsWith('*');
      
      const parts = line.split(/(\*\*.*?\*\*)/g);
      return (
        <div key={i} className={`mb-1 ${isListItem ? 'pl-4 border-l-2 border-[#D4FF33]/20 my-1' : ''}`}>
          {parts.map((part, index) => {
            if (part.startsWith('**') && part.endsWith('**')) {
              return <strong key={index} className="text-[#D4FF33] font-bold">{part.slice(2, -2)}</strong>;
            }
            return <span key={index}>{part}</span>;
          })}
        </div>
      );
    });
  };

  const suggestions = ["üèãÔ∏è Chest Workout", "ü•ó Diet Plan", "ü¶µ Leg Day", "üç´ Abs Routine"];

  return (
    <div className="flex flex-col h-[calc(100vh-85px)] bg-[#050505] text-white font-sans relative overflow-hidden">
      
      {/* Background Aesthetic Glow */}
      <div className="absolute top-[-10%] left-[-10%] w-[300px] h-[300px] bg-[#D4FF33] rounded-full mix-blend-screen filter blur-[100px] opacity-10 pointer-events-none"></div>

      {/* --- HEADER --- */}
      <div className="flex items-center justify-between px-6 py-4 backdrop-blur-md bg-black/40 border-b border-white/5 sticky top-0 z-10">
        <div className="flex items-center gap-4">
          <div className="w-11 h-11 rounded-xl bg-gradient-to-tr from-[#D4FF33] to-[#a3cc00] flex items-center justify-center shadow-lg shadow-[#D4FF33]/20">
            <Bot size={24} className="text-black" />
          </div>
          <div>
            <h1 className="text-xl font-black tracking-tight uppercase">AI Coach</h1>
            <div className="flex items-center gap-1.5 text-[10px] text-gray-400 font-medium uppercase tracking-widest">
              <Activity size={10} className="text-[#D4FF33] animate-pulse" />
              <span>Real-time Assistant</span>
            </div>
          </div>
        </div>
      </div>

      {/* --- CHAT AREA --- */}
      <div className="flex-1 overflow-y-auto p-4 space-y-6 custom-scrollbar">
        {messages.map((msg, index) => (
          <div key={index} className={`flex gap-3 ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            {msg.role === 'ai' && (
              <div className="w-8 h-8 rounded-full bg-[#1e1e1e] border border-gray-800 flex items-center justify-center flex-shrink-0 mt-1">
                 <Sparkles size={14} className="text-[#D4FF33]" />
              </div>
            )}

            <div className={`p-4 rounded-2xl max-w-[85%] md:max-w-[70%] text-sm leading-relaxed shadow-sm ${
              msg.role === 'user' 
                ? 'bg-[#D4FF33] text-black font-semibold rounded-tr-none' 
                : 'bg-[#121212] text-gray-200 border border-white/5 rounded-tl-none'
            }`}>
              <FormatMessage content={msg.content} />
            </div>
          </div>
        ))}
        
        {loading && (
          <div className="flex gap-3 ml-11">
              <div className="bg-[#121212] px-5 py-3 rounded-2xl rounded-tl-none border border-white/5 flex items-center gap-1.5">
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce [animation-delay:-0.3s]"></div>
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce [animation-delay:-0.15s]"></div>
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce"></div>
              </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* --- INPUT AREA --- */}
      <div className="p-4 bg-black/90 backdrop-blur-2xl border-t border-white/5">
        
        {/* Suggestion Chips */}
        {messages.length < 4 && (
          <div className="flex gap-2 overflow-x-auto pb-3 no-scrollbar">
            {suggestions.map((text, idx) => (
              <button 
                key={idx} 
                onClick={() => handleSend(text)} 
                className="bg-[#111] border border-white/10 text-gray-300 px-4 py-2 rounded-full text-xs hover:bg-[#D4FF33] hover:text-black hover:border-[#D4FF33] whitespace-nowrap transition-all duration-300 active:scale-95"
              >
                {text}
              </button>
            ))}
          </div>
        )}

        {/* Form Input */}
        <div className="relative flex items-center gap-3">
          <input 
            type="text" 
            className="w-full bg-[#0F0F0F] border border-white/10 rounded-2xl p-4 text-sm text-white placeholder-gray-600 focus:border-[#D4FF33] focus:ring-1 focus:ring-[#D4FF33]/20 focus:outline-none transition-all"
            placeholder="Describe your workout or ask for a diet plan..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button 
            onClick={() => handleSend()}
            disabled={loading || !input.trim()}
            className="p-4 bg-[#D4FF33] rounded-2xl text-black hover:bg-[#c5ec2b] hover:shadow-[0_0_20px_rgba(212,255,51,0.3)] disabled:opacity-50 disabled:hover:shadow-none transition-all active:scale-95"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
      
      <style jsx>{`
        .no-scrollbar::-webkit-scrollbar { display: none; }
        .no-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-track { background: transparent; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #222; border-radius: 10px; }
      `}</style>
    </div>
  );
};

export default AICoach;
