import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { X, Flame, Search, Check } from 'lucide-react';

const MealModel = ({ isOpen, onClose, onSave }) => {
  const { user } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  
  // Form State
  const [formData, setFormData] = useState({ 
    mealType: 'Breakfast', 
    foodName: '', 
    calories: '', 
    protein: '', 
    carbs: '', 
    fat: '' 
  });

  // Search State
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [showResults, setShowResults] = useState(false);

  // Search API Call
  useEffect(() => {
    const searchFoods = async () => {
      if (query.length < 2) {
        setResults([]);
        return;
      }
      try {
        const res = await fetch(`http://localhost:5000/api/foods/search?query=${query}`);
        const data = await res.json();
        setResults(data);
        setShowResults(true);
      } catch (err) { console.error(err); }
    };
    
    // Debounce (wait 300ms after typing stops)
    const delayDebounceFn = setTimeout(() => {
      searchFoods();
    }, 300);

    return () => clearTimeout(delayDebounceFn);
  }, [query]);

  // Handle Selection
  const selectFood = (food) => {
    setFormData({
      ...formData,
      foodName: food.name,
      calories: food.calories,
      protein: food.protein,
      carbs: food.carbs,
      fat: food.fat
    });
    setQuery(''); // Clear search
    setShowResults(false); // Hide list
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await fetch('http://localhost:5000/api/nutrition', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${user.token}` },
        body: JSON.stringify(formData)
      });
      onSave(); onClose();
      setFormData({ mealType: 'Breakfast', foodName: '', calories: '', protein: '', carbs: '', fat: '' });
    } catch (error) { console.error(error); } finally { setLoading(false); }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center z-[60] p-4">
      <div className="bg-[#121212] border border-gray-800 w-full max-w-lg rounded-2xl p-6 shadow-2xl relative">
        
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-bold text-white flex items-center gap-2">
            <Flame className="text-[#D4FF33]" size={24} /> Log Meal
          </h2>
          <button onClick={onClose} className="text-gray-400 hover:text-white"><X size={24} /></button>
        </div>

        {/* --- SMART SEARCH BAR --- */}
        <div className="relative mb-6">
          <div className="absolute left-3 top-3 text-gray-500"><Search size={18} /></div>
          <input 
            type="text" 
            placeholder="Search food database (e.g. 'Chicken', 'Egg')" 
            className="w-full bg-[#0A0A0A] border border-gray-700 rounded-xl p-3 pl-10 text-white focus:border-[#D4FF33] focus:outline-none"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          
          {/* Search Results Dropdown */}
          {showResults && results.length > 0 && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-[#1e1e1e] border border-gray-700 rounded-xl shadow-2xl z-50 max-h-48 overflow-y-auto">
              {results.map((food) => (
                <div 
                  key={food._id}
                  onClick={() => selectFood(food)}
                  className="p-3 hover:bg-[#D4FF33] hover:text-black cursor-pointer border-b border-gray-800 last:border-0 transition-colors"
                >
                  <div className="font-bold text-sm">{food.name}</div>
                  <div className="text-xs opacity-70">
                    {food.calories} cal • P: {food.protein}g • C: {food.carbs}g • F: {food.fat}g
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          
          {/* Meal Type */}
          <div>
            <label className="text-xs text-gray-500 mb-1 block">Meal Type</label>
            <div className="flex gap-2">
              {['Breakfast', 'Lunch', 'Dinner', 'Snack'].map(type => (
                <button
                  type="button"
                  key={type}
                  onClick={() => setFormData({...formData, mealType: type})}
                  className={`flex-1 py-2 text-xs rounded-lg border transition-all ${
                    formData.mealType === type 
                      ? 'bg-[#D4FF33] text-black border-[#D4FF33] font-bold' 
                      : 'bg-transparent text-gray-400 border-gray-800 hover:border-gray-600'
                  }`}
                >
                  {type}
                </button>
              ))}
            </div>
          </div>

          {/* Food Name (Auto-filled or Manual) */}
          <div>
             <label className="text-xs text-gray-500 mb-1 block">Food Name</label>
             <input type="text" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.foodName} onChange={(e) => setFormData({...formData, foodName: e.target.value})} required />
          </div>

          {/* Macros Grid */}
          <div className="grid grid-cols-2 gap-4">
            <div><label className="text-xs text-gray-500 mb-1 block">Calories</label><input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.calories} onChange={(e) => setFormData({...formData, calories: e.target.value})} required /></div>
            <div><label className="text-xs text-gray-500 mb-1 block">Protein (g)</label><input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.protein} onChange={(e) => setFormData({...formData, protein: e.target.value})} /></div>
            <div><label className="text-xs text-gray-500 mb-1 block">Carbs (g)</label><input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.carbs} onChange={(e) => setFormData({...formData, carbs: e.target.value})} /></div>
            <div><label className="text-xs text-gray-500 mb-1 block">Fat (g)</label><input type="number" className="w-full bg-[#0A0A0A] border border-gray-800 rounded-lg p-3 text-white focus:border-[#D4FF33] focus:outline-none" value={formData.fat} onChange={(e) => setFormData({...formData, fat: e.target.value})} /></div>
          </div>

          <button disabled={loading} type="submit" className="w-full bg-[#D4FF33] text-black font-bold py-3 rounded-xl mt-4 hover:opacity-90 transition">
            {loading ? 'Saving...' : 'Save Meal'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default MealModel;
