import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Activity, Flame, Footprints, Dumbbell, Moon, Droplets, Scale, 
  Plus, Calendar, TrendingUp, Zap, ChevronRight, CheckCircle2 
} from 'lucide-react';
import { 
  BarChart, Bar, XAxis, Tooltip, ResponsiveContainer, Cell 
} from 'recharts';

// Import Your Modals
import WorkoutModel from '../components/WorkoutModel';
import MealModel from '../components/MealModel';
import BodyStatsModel from '../components/BodyStatsModel'; 

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const todayDate = new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' });

  // --- UI State ---
  const [showQuickLogMenu, setShowQuickLogMenu] = useState(false);
  const [activeModal, setActiveModal] = useState(null); 
  const [waterLevel, setWaterLevel] = useState(0); // Local state for demo

  // --- Data State ---
  const [dashboardData, setDashboardData] = useState({
    caloriesIn: 0, caloriesBurned: 0,
    protein: 0, carbs: 0, fat: 0,
    workoutsCount: 0, steps: 0,
    weight: 0, muscleMass: 0,
    height: 175 // Default fallback
  });
  const [graphData, setGraphData] = useState([]);
  const [recentWorkouts, setRecentWorkouts] = useState([]);

  // --- 1. Daily Focus Logic ---
  const getDailyFocus = () => {
      const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
      const today = days[new Date().getDay()];
      const schedule = {
        'Monday': { focus: 'Chest & Triceps', icon: 'ðŸ’ª', color: 'text-red-400', border: 'border-red-500/30', gradient: 'from-red-500/20 to-transparent' },
        'Tuesday': { focus: 'Back & Biceps', icon: 'ðŸ‹ï¸', color: 'text-blue-400', border: 'border-blue-500/30', gradient: 'from-blue-500/20 to-transparent' },
        'Wednesday': { focus: 'Legs & Core', icon: 'ðŸ¦µ', color: 'text-orange-400', border: 'border-orange-500/30', gradient: 'from-orange-500/20 to-transparent' },
        'Thursday': { focus: 'Shoulders & Abs', icon: 'ðŸ§˜', color: 'text-purple-400', border: 'border-purple-500/30', gradient: 'from-purple-500/20 to-transparent' },
        'Friday': { focus: 'Full Body HIIT', icon: 'ðŸ”¥', color: 'text-yellow-400', border: 'border-yellow-500/30', gradient: 'from-yellow-500/20 to-transparent' },
        'Saturday': { focus: 'Yoga & Recovery', icon: 'ðŸ§˜', color: 'text-green-400', border: 'border-green-500/30', gradient: 'from-green-500/20 to-transparent' },
        'Sunday': { focus: 'Rest Day', icon: 'ðŸ˜´', color: 'text-gray-400', border: 'border-gray-500/30', gradient: 'from-gray-500/20 to-transparent' }
      };
      return { day: today, ...schedule[today] };
  };
  const dailySuggestion = getDailyFocus();

  // --- 2. Fetch Data ---
  const fetchData = async () => {
      try {
        const headers = { 'Authorization': `Bearer ${user.token}` };
        
        const [workoutsRes, nutritionRes, statsRes, profileRes] = await Promise.all([
          fetch('http://localhost:5000/api/workouts', { headers }),
          fetch('http://localhost:5000/api/nutrition', { headers }),
          fetch('http://localhost:5000/api/stats', { headers }),
          fetch('http://localhost:5000/api/users/profile', { headers })
        ]);

        const workouts = await workoutsRes.json();
        const nutrition = await nutritionRes.json();
        const stats = await statsRes.json();
        const profile = await profileRes.json();

        const todayStr = new Date().toDateString();

        // Nutrition Totals
        const todaysMeals = nutrition.filter(n => new Date(n.date).toDateString() === todayStr);
        const totals = todaysMeals.reduce((acc, curr) => ({
            cal: acc.cal + (curr.calories || 0),
            pro: acc.pro + (curr.protein || 0),
            carb: acc.carb + (curr.carbs || 0),
            fat: acc.fat + (curr.fat || 0)
        }), { cal: 0, pro: 0, carb: 0, fat: 0 });

        // Workouts
        const todaysWorkouts = workouts.filter(w => new Date(w.date).toDateString() === todayStr);
        const calOut = todaysWorkouts.reduce((acc, curr) => acc + (curr.caloriesBurned || 0), 0);
        
        // Body Stats
        const sortedStats = stats.sort((a, b) => new Date(b.date) - new Date(a.date));
        const latestStat = sortedStats[0] || {};

        setDashboardData({
            caloriesIn: totals.cal,
            caloriesBurned: calOut,
            protein: totals.pro,
            carbs: totals.carb,
            fat: totals.fat,
            workoutsCount: todaysWorkouts.length,
            steps: latestStat.steps || 0, 
            weight: latestStat.weight || '--',
            muscleMass: latestStat.muscleMass || '--',
            height: profile.height || 175
        });

        setRecentWorkouts(workouts.slice(0, 3));

        // Graph Data
        const last7Days = Array.from({ length: 7 }, (_, i) => { 
            const d = new Date(); d.setDate(d.getDate() - (6 - i)); return d; 
        });
        const activityGraph = last7Days.map(date => {
            const dateStr = date.toDateString();
            const dayMins = workouts
                .filter(w => new Date(w.date).toDateString() === dateStr)
                .reduce((acc, curr) => acc + (curr.duration || 0), 0);
            return { day: date.toLocaleDateString('en-US', { weekday: 'short' }), active: dayMins };
        });
        setGraphData(activityGraph);

      } catch (error) { console.error(error); }
  };

  useEffect(() => { if (user) fetchData(); }, [user]);

  // Calculate BMI
  const getBMI = () => {
      if(dashboardData.weight > 0 && dashboardData.height > 0) {
          const h = dashboardData.height / 100;
          return (dashboardData.weight / (h * h)).toFixed(1);
      }
      return '--';
  };
  const bmi = getBMI();

  return (
    <div className="min-h-screen bg-[#0A0A0A] p-6 text-white pb-24">
      
      {/* HEADER */}
      <div className="flex justify-between items-start mb-8 relative">
        <div>
          <h1 className="text-3xl font-bold text-white">Hello, {user?.name?.split(' ')[0]}! ðŸ‘‹</h1>
          <p className="text-gray-400 text-sm mt-1">Let's crush your {dailySuggestion.day} goals.</p>
        </div>
        
        <div className="text-right relative">
          <button 
            onClick={() => setShowQuickLogMenu(!showQuickLogMenu)}
            className="bg-[#D4FF33] text-black font-bold px-4 py-2 rounded-xl flex items-center gap-2 hover:opacity-90 mb-2 shadow-lg shadow-[#D4FF33]/20 transition-all"
          >
            <Plus size={18} /> Quick Log
          </button>
          
          {showQuickLogMenu && (
            <div className="absolute right-0 top-12 bg-[#1e1e1e] border border-gray-700 rounded-xl shadow-2xl p-2 w-48 z-50 flex flex-col gap-1 animate-in fade-in zoom-in-95 duration-200">
              <button onClick={() => { setActiveModal('workout'); setShowQuickLogMenu(false); }} className="flex items-center gap-3 px-3 py-2.5 hover:bg-gray-800 rounded-lg text-sm text-gray-300 hover:text-white transition-colors text-left"><Dumbbell size={16} className="text-[#D4FF33]"/> Log Workout</button>
              <button onClick={() => { setActiveModal('meal'); setShowQuickLogMenu(false); }} className="flex items-center gap-3 px-3 py-2.5 hover:bg-gray-800 rounded-lg text-sm text-gray-300 hover:text-white transition-colors text-left"><Flame size={16} className="text-orange-500"/> Log Meal</button>
              <button onClick={() => { setActiveModal('stats'); setShowQuickLogMenu(false); }} className="flex items-center gap-3 px-3 py-2.5 hover:bg-gray-800 rounded-lg text-sm text-gray-300 hover:text-white transition-colors text-left"><Scale size={16} className="text-blue-500"/> Log Weight</button>
            </div>
          )}
        </div>
      </div>

      {/* SECTION 1: FOCUS & NUTRITION */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        
        {/* Daily Focus Card */}
        <div className={`p-6 rounded-2xl relative overflow-hidden border bg-gradient-to-br ${dailySuggestion.gradient} ${dailySuggestion.border}`}>
          <div className="flex items-center gap-2 mb-4">
            <div className={`p-1.5 rounded-md bg-black/20 ${dailySuggestion.color}`}>
               <Calendar size={16} />
            </div>
            <h3 className={`font-bold text-sm uppercase tracking-wide ${dailySuggestion.color}`}>Today's Focus</h3>
          </div>
          <div className="mb-6 relative z-10">
             <h2 className="text-3xl font-bold text-white mb-1 flex items-center gap-2">
                {dailySuggestion.icon} {dailySuggestion.focus}
             </h2>
             <p className="text-sm text-gray-400">Your plan for {dailySuggestion.day}.</p>
          </div>
          <button onClick={() => setActiveModal('workout')} className="w-full bg-[#1e1e1e]/80 hover:bg-black text-white border border-gray-700 font-bold py-3 rounded-xl transition text-sm flex items-center justify-center gap-2 backdrop-blur-sm">
             Start Training <ChevronRight size={14} />
          </button>
        </div>

        {/* Nutrition Breakdown */}
        <div className="lg:col-span-2 bg-[#121212] border border-gray-800 p-6 rounded-2xl flex flex-col justify-between">
          <div className="flex justify-between items-center mb-6">
            <h3 className="font-bold text-lg flex items-center gap-2"><Flame className="text-orange-500" size={20} /> Nutrition</h3>
            <span className="text-xs text-gray-500 font-mono bg-gray-900 px-2 py-1 rounded-md">Goal: 2500 kcal</span>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 items-center">
             {/* Total Calories */}
             <div className="flex items-center gap-4">
                 <div className="w-20 h-20 rounded-full border-4 border-[#1e1e1e] border-t-orange-500 flex items-center justify-center flex-col">
                     <span className="font-bold text-xl text-white">{dashboardData.caloriesIn}</span>
                     <span className="text-[10px] text-gray-500 uppercase">kcal</span>
                 </div>
                 <div>
                    <div className="text-xs text-gray-400 mb-1">Burned Today</div>
                    <div className="text-lg font-bold text-[#D4FF33] flex items-center gap-1"><Zap size={14}/> {dashboardData.caloriesBurned}</div>
                 </div>
             </div>

             {/* Macro Bars */}
             <div className="md:col-span-2 space-y-3">
                <div>
                   <div className="flex justify-between text-xs mb-1"><span className="text-blue-400 font-bold">Protein</span><span className="text-gray-400">{dashboardData.protein} / 180g</span></div>
                   <div className="h-2 w-full bg-gray-800 rounded-full overflow-hidden"><div className="h-full bg-blue-500" style={{ width: `${Math.min((dashboardData.protein/180)*100, 100)}%` }}></div></div>
                </div>
                <div>
                   <div className="flex justify-between text-xs mb-1"><span className="text-yellow-400 font-bold">Carbs</span><span className="text-gray-400">{dashboardData.carbs} / 250g</span></div>
                   <div className="h-2 w-full bg-gray-800 rounded-full overflow-hidden"><div className="h-full bg-yellow-500" style={{ width: `${Math.min((dashboardData.carbs/250)*100, 100)}%` }}></div></div>
                </div>
                <div>
                   <div className="flex justify-between text-xs mb-1"><span className="text-red-400 font-bold">Fats</span><span className="text-gray-400">{dashboardData.fat} / 80g</span></div>
                   <div className="h-2 w-full bg-gray-800 rounded-full overflow-hidden"><div className="h-full bg-red-500" style={{ width: `${Math.min((dashboardData.fat/80)*100, 100)}%` }}></div></div>
                </div>
             </div>
          </div>
        </div>
      </div>

      {/* SECTION 2: METRICS */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        
        {/* Weight & BMI */}
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-2xl">
           <div className="flex justify-between mb-2">
              <Scale className="text-yellow-500" size={20} />
              <span className={`text-[10px] px-2 py-0.5 rounded font-bold ${bmi > 25 ? 'bg-red-500/20 text-red-400' : 'bg-green-500/20 text-green-400'}`}>BMI {bmi}</span>
           </div>
           <h3 className="text-2xl font-bold text-white">{dashboardData.weight} <span className="text-sm text-gray-500">kg</span></h3>
           <p className="text-xs text-gray-500 mt-1">Current Weight</p>
        </div>

        {/* Steps */}
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-2xl">
           <div className="flex justify-between mb-2"><Footprints className="text-green-500" size={20} /></div>
           <h3 className="text-2xl font-bold text-white">{dashboardData.steps.toLocaleString()}</h3>
           <p className="text-xs text-gray-500 mt-1">Daily Steps</p>
        </div>

        {/* Water Interactive */}
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-2xl relative overflow-hidden group">
           <div className="absolute right-0 bottom-0 opacity-10 group-hover:opacity-20 transition-opacity"><Droplets size={60} className="text-blue-500"/></div>
           <div className="flex justify-between mb-2"><Droplets className="text-blue-500" size={20} /></div>
           <div className="flex items-end gap-2">
              <h3 className="text-2xl font-bold text-white">{waterLevel} <span className="text-sm text-gray-500">/ 8</span></h3>
              <button onClick={() => setWaterLevel(p => p < 8 ? p+1 : p)} className="mb-1 p-1 bg-blue-500/20 hover:bg-blue-500 text-blue-500 hover:text-white rounded-md transition"><Plus size={14}/></button>
           </div>
           <p className="text-xs text-gray-500 mt-1">Glasses of Water</p>
        </div>

        {/* Weekly Consistency */}
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-2xl">
           <div className="flex justify-between mb-2"><CheckCircle2 className="text-[#D4FF33]" size={20} /></div>
           <h3 className="text-2xl font-bold text-white">3 <span className="text-sm text-gray-500">/ 5</span></h3>
           <div className="w-full bg-gray-800 h-1.5 rounded-full mt-2 overflow-hidden">
               <div className="bg-[#D4FF33] h-full" style={{width: '60%'}}></div>
           </div>
           <p className="text-xs text-gray-500 mt-2">Weekly Goal</p>
        </div>
      </div>

      {/* SECTION 3: ACTIVITY GRAPH & HISTORY */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-[#121212] border border-gray-800 p-6 rounded-2xl h-80">
          <div className="flex justify-between items-center mb-6">
            <h3 className="font-bold flex items-center gap-2"><TrendingUp size={20} className="text-[#D4FF33]" /> Activity Volume</h3>
            <span className="text-xs text-gray-500">Last 7 Days</span>
          </div>
          <ResponsiveContainer width="100%" height="80%">
            <BarChart data={graphData}>
              <Tooltip cursor={{fill: 'rgba(255, 255, 255, 0.05)'}} contentStyle={{ backgroundColor: '#1e1e1e', borderColor: '#333', color: '#fff', borderRadius: '8px' }} />
              <XAxis dataKey="day" axisLine={false} tickLine={false} tick={{ fill: '#666', fontSize: 12 }} dy={10} />
              <Bar dataKey="active" radius={[4, 4, 0, 0]} barSize={30}>
                {graphData.map((entry, i) => (<Cell key={i} fill={entry.active > 0 ? '#D4FF33' : '#222'} />))}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-[#121212] border border-gray-800 p-6 rounded-2xl">
           <h3 className="font-bold mb-4">Recent Activity</h3>
           <div className="space-y-4">
              {recentWorkouts.length === 0 ? <p className="text-gray-500 text-sm">No recent activity.</p> : recentWorkouts.map((w) => (
                  <div key={w._id} className="flex items-center gap-3 pb-3 border-b border-gray-800 last:border-0">
                      <div className="bg-gray-800 p-2 rounded-lg text-[#D4FF33]"><Dumbbell size={16} /></div>
                      <div className="flex-1">
                          <h4 className="text-sm font-bold text-white truncate">{w.title}</h4>
                          <p className="text-xs text-gray-500">{new Date(w.date).toLocaleDateString()}</p>
                      </div>
                      <span className="text-xs font-bold text-gray-400">-{w.caloriesBurned} cal</span>
                  </div>
              ))}
           </div>
        </div>
      </div>

      {/* MODALS */}
      <WorkoutModel isOpen={activeModal === 'workout'} onClose={() => setActiveModal(null)} onSave={fetchData} />
      <MealModel isOpen={activeModal === 'meal'} onClose={() => setActiveModal(null)} onSave={fetchData} />
      <BodyStatsModel isOpen={activeModal === 'stats'} onClose={() => setActiveModal(null)} onSave={fetchData} />

    </div>
  );
};

export default Dashboard;
