import React, { useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, AuthContext } from './context/AuthContext';
import { ThemeProvider, ThemeContext } from './context/ThemeContext'; // Import Theme

import BottomNav from './components/BottomNav';
import Dashboard from './pages/Dashboard';
import Login from './pages/Login';
import Register from './pages/Register';
import Onboarding from './pages/Onboarding';
import Workouts from './pages/Workouts';
import Nutrition from './pages/Nutrition';
import AICoach from './pages/AICoach';
import Profile from './pages/Profile'; // Import Profile

// App Content Wrapper to use ThemeContext
const AppContent = () => {
  const { user } = useContext(AuthContext);
  const { styles } = useContext(ThemeContext); // Access dynamic styles

  return (
    // Dynamic Background Class applied here
    <div className={`min-h-screen ${styles.bg} ${styles.text} font-sans transition-colors duration-300`}>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        <Route path="/onboarding" element={user ? <Onboarding /> : <Navigate to="/login" />} />
        <Route path="/dashboard" element={user ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/workouts" element={user ? <Workouts /> : <Navigate to="/login" />} />
        <Route path="/nutrition" element={user ? <Nutrition /> : <Navigate to="/login" />} />
        <Route path="/ai-coach" element={user ? <AICoach /> : <Navigate to="/login" />} />

        {/* Profile Route */}
        <Route path="/profile" element={user ? <Profile /> : <Navigate to="/login" />} />

        <Route path="/" element={<Navigate to={user ? "/dashboard" : "/login"} />} />
      </Routes>

      {user && <BottomNav />}
    </div>
  );
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <ThemeProvider>
          <AppContent />
        </ThemeProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
