import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Plus, Scale, Percent, Heart, Footprints, Calendar, TrendingUp 
} from 'lucide-react';
import { 
  ResponsiveContainer, LineChart, Line, XAxis, Tooltip, YAxis 
} from 'recharts';
import BodyStatsModel from '../components/BodyStatsModel'; // Import your Modal

const BodyStats = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch Data
  const fetchStats = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/stats', {
        headers: { 'Authorization': `Bearer ${user.token}` }
      });
      const data = await res.json();
      setStats(data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { if(user) fetchStats(); }, [user]);

  // Latest Entry
  const latest = stats[0] || {}; 

  // Graph Data (Reverse so oldest is left, newest is right)
  const graphData = [...stats].reverse().map(s => ({
    date: new Date(s.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
    weight: s.weight
  }));

  const StatCard = ({ label, value, unit, icon: Icon, color }) => (
    <div className="bg-[#121212] border border-gray-800 p-6 rounded-2xl relative overflow-hidden">
      <div className="flex justify-between items-start mb-2">
        <div className={`p-2 rounded-lg bg-opacity-10 ${color}`}>
          <Icon size={20} className={color.replace('bg-', 'text-')} />
        </div>
      </div>
      <p className="text-xs text-gray-500 uppercase font-bold tracking-wider mb-1">{label}</p>
      <div className="flex items-baseline gap-1">
         <span className="text-3xl font-bold text-white">{value || '--'}</span>
         <span className="text-sm text-gray-400 font-medium">{unit}</span>
      </div>
    </div>
  );

  return (
    <div className="p-6 pb-24 min-h-screen">
      
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold mb-1">Body Stats</h1>
          <p className="text-gray-400">Track your measurements and progress</p>
        </div>
        <button 
          onClick={() => setIsModalOpen(true)}
          className="bg-[#D4FF33] text-black font-bold px-5 py-2.5 rounded-xl flex items-center gap-2 hover:opacity-90 transition-all shadow-lg shadow-[#D4FF33]/20"
        >
          <Plus size={20} /> Log Stats
        </button>
      </div>

      {/* Top Row: Cards */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <StatCard label="Weight" value={latest.weight} unit="kg" icon={Scale} color="bg-yellow-500 text-yellow-500" />
        <StatCard label="Body Fat" value={latest.bodyFat} unit="%" icon={Percent} color="bg-blue-500 text-blue-500" />
        <StatCard label="Heart Rate" value={latest.heartRate} unit="bpm" icon={Heart} color="bg-red-500 text-red-500" />
        <StatCard label="Steps" value={latest.steps} unit="" icon={Footprints} color="bg-green-500 text-green-500" />
      </div>

      {/* Detailed Measurements (Optional based on screenshot) */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
         <div className="bg-[#121212] border border-gray-800 p-4 rounded-xl">
            <span className="text-gray-500 text-xs block mb-1">Muscle Mass</span>
            <span className="text-xl font-bold">{latest.muscleMass || '--'} <span className="text-xs text-gray-500">kg</span></span>
         </div>
         <div className="bg-[#121212] border border-gray-800 p-4 rounded-xl">
            <span className="text-gray-500 text-xs block mb-1">Waist</span>
            <span className="text-xl font-bold">{latest.waist || '--'} <span className="text-xs text-gray-500">cm</span></span>
         </div>
         <div className="bg-[#121212] border border-gray-800 p-4 rounded-xl">
            <span className="text-gray-500 text-xs block mb-1">Chest</span>
            <span className="text-xl font-bold">{latest.chest || '--'} <span className="text-xs text-gray-500">cm</span></span>
         </div>
      </div>

      {/* Graph Section */}
      <div className="bg-[#121212] border border-gray-800 p-6 rounded-2xl mb-8 h-80 relative">
        <div className="flex items-center gap-2 mb-6">
            <TrendingUp size={20} className="text-[#D4FF33]" />
            <h3 className="font-bold">Weight Trend</h3>
        </div>
        <ResponsiveContainer width="100%" height="80%">
          <LineChart data={graphData}>
            <XAxis 
              dataKey="date" 
              stroke="#555" 
              fontSize={12} 
              tickLine={false} 
              axisLine={false} 
              dy={10}
            />
            <YAxis 
              stroke="#555" 
              fontSize={12} 
              tickLine={false} 
              axisLine={false} 
              domain={['dataMin - 2', 'dataMax + 2']} 
              dx={-10}
            />
            <Tooltip 
              contentStyle={{ backgroundColor: '#1e1e1e', borderColor: '#333', color: '#fff', borderRadius: '8px' }}
              itemStyle={{ color: '#D4FF33' }}
              cursor={{ stroke: '#333', strokeWidth: 2 }}
            />
            <Line 
              type="monotone" 
              dataKey="weight" 
              stroke="#D4FF33" 
              strokeWidth={3} 
              dot={{r: 4, fill:'#0A0A0A', stroke:'#D4FF33', strokeWidth: 2}} 
              activeDot={{r: 6, fill:'#D4FF33'}} 
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* History List */}
      <div className="space-y-4">
        <h3 className="font-bold text-lg">Recent Entries</h3>
        {stats.map((item) => (
           <div key={item._id} className="bg-[#121212] border border-gray-800 p-4 rounded-xl flex justify-between items-center text-sm hover:border-gray-700 transition-colors">
             <div className="flex items-center gap-4 text-gray-400">
               <div className="p-2 bg-gray-900 rounded-lg"><Calendar size={16} /></div>
               {new Date(item.date).toDateString()}
             </div>
             <div className="flex gap-6 font-medium">
               <span className="text-white">{item.weight} <span className="text-gray-600 text-xs">kg</span></span>
               <span className="text-blue-400">{item.bodyFat}%</span>
               <span className="text-green-500">{item.steps} <span className="text-gray-600 text-xs">steps</span></span>
             </div>
           </div>
        ))}
      </div>

      {/* Modal Popup */}
      <BodyStatsModel 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        onSave={fetchStats} 
      />
    </div>
  );
};

export default BodyStats;
