import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Activity, Flame, Footprints, Moon, Droplets, 
  Dumbbell, TrendingUp, Calendar 
} from 'lucide-react';
import { 
  BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell 
} from 'recharts';

// Mock Data for the Graph
const weeklyData = [
  { day: 'Mon', active: 45 },
  { day: 'Tue', active: 60 },
  { day: 'Wed', active: 30 },
  { day: 'Thu', active: 75 },
  { day: 'Fri', active: 50 },
  { day: 'Sat', active: 90 },
  { day: 'Sun', active: 20 },
];

const Dashboard = () => {
  const { user } = useContext(AuthContext);

  // Reusable Component for the Top Summary Row
  const SummaryCard = ({ icon: Icon, label, value, subtext, color }) => (
    <div className="bg-card border border-gray-800 p-4 rounded-xl flex items-center gap-4">
      <div className={`p-3 rounded-lg ${color} bg-opacity-10 text-white`}>
        <Icon size={24} className={color.replace('bg-', 'text-')} />
      </div>
      <div>
        <p className="text-gray-400 text-xs uppercase font-bold tracking-wider">{label}</p>
        <div className="flex items-baseline gap-1">
           <h3 className="text-xl font-bold text-white">{value}</h3>
           <span className="text-xs text-gray-500">{subtext}</span>
        </div>
      </div>
    </div>
  );

  // Reusable Component for Detailed Metric Cards
  const MetricCard = ({ title, value, goal, unit, icon: Icon, progressColor, details }) => {
    const percentage = Math.min((value / goal) * 100, 100);
    
    return (
      <div className="bg-card border border-gray-800 p-6 rounded-2xl relative overflow-hidden">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h3 className="text-gray-400 font-medium text-sm flex items-center gap-2">
              <Icon size={16} /> {title}
            </h3>
            <div className="mt-1">
              <span className="text-3xl font-bold text-white">{value}</span>
              <span className="text-gray-500 text-sm ml-1">/ {goal} {unit}</span>
            </div>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="h-2 w-full bg-gray-800 rounded-full mb-4">
          <div 
            className={`h-full rounded-full ${progressColor}`} 
            style={{ width: `${percentage}%` }}
          ></div>
        </div>

        {/* Sub-details row */}
        <div className="flex justify-between text-xs text-gray-400 border-t border-gray-800 pt-3">
          {details.map((detail, idx) => (
            <div key={idx}>
              <span className="block text-white font-semibold">{detail.val}</span>
              <span>{detail.label}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-background p-6 pb-24 text-white">
      {/* Header */}
      <div className="flex justify-between items-end mb-8">
        <div>
          <h1 className="text-3xl font-bold">Welcome back, {user?.name || 'Dhruv'}!</h1>
          <p className="text-gray-400">Here's your fitness breakdown for today.</p>
        </div>
        <button className="bg-primary text-black font-bold px-4 py-2 rounded-lg text-sm hover:opacity-90">
          + Quick Log
        </button>
      </div>

      {/* 1. Top Summary Row */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <SummaryCard icon={Flame} label="Calories" value="1,245" subtext="/ 2,000" color="bg-orange-500 text-orange-500" />
        <SummaryCard icon={Footprints} label="Steps" value="8,432" subtext="/ 10k" color="bg-green-500 text-green-500" />
        <SummaryCard icon={Activity} label="Active Mins" value="45" subtext="/ 60" color="bg-pink-500 text-pink-500" />
        <SummaryCard icon={Moon} label="Sleep" value="7h 15m" subtext="Goal: 8h" color="bg-indigo-500 text-indigo-500" />
      </div>

      {/* 2. Main Grid Layout */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        
        {/* Left Column: Metrics */}
        <div className="md:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-6">
          <MetricCard 
            title="Steps" 
            value={8432} 
            goal={10000} 
            unit="steps" 
            icon={Footprints} 
            progressColor="bg-green-500"
            details={[{val: '1.2km', label: 'Distance'}, {val: '420', label: 'Cal Burned'}]} 
          />
          <MetricCard 
            title="Nutrition" 
            value={1245} 
            goal={2000} 
            unit="cal" 
            icon={Flame} 
            progressColor="bg-blue-500"
            details={[{val: '86g', label: 'Protein'}, {val: '145g', label: 'Carbs'}, {val: '45g', label: 'Fat'}]} 
          />
          <MetricCard 
            title="Hydration" 
            value={5} 
            goal={8} 
            unit="glasses" 
            icon={Droplets} 
            progressColor="bg-cyan-400"
            details={[{val: '1.2L', label: 'Consumed'}, {val: '250ml', label: 'Avg Size'}]} 
          />
          <MetricCard 
            title="Workouts" 
            value={1} 
            goal={2} 
            unit="sessions" 
            icon={Dumbbell} 
            progressColor="bg-pink-500"
            details={[{val: 'Upper Body', label: 'Focus'}, {val: '45m', label: 'Duration'}]} 
          />
        </div>

        {/* Right Column: Weekly Graph */}
        <div className="bg-card border border-gray-800 p-6 rounded-2xl flex flex-col">
          <div className="flex justify-between items-center mb-6">
            <h3 className="font-bold flex items-center gap-2">
              <TrendingUp size={18} className="text-primary" /> Activity Trend
            </h3>
            <select className="bg-[#0A0A0A] border border-gray-700 rounded-md text-xs p-1 text-gray-400 outline-none">
              <option>This Week</option>
              <option>Last Week</option>
            </select>
          </div>

          <div className="h-64 w-full">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={weeklyData}>
                <Tooltip 
                  contentStyle={{ backgroundColor: '#121212', borderColor: '#333', color: '#fff' }}
                  cursor={{fill: 'rgba(255, 255, 255, 0.05)'}} 
                />
                <XAxis 
                  dataKey="day" 
                  axisLine={false} 
                  tickLine={false} 
                  tick={{ fill: '#666', fontSize: 12 }} 
                  dy={10}
                />
                <Bar dataKey="active" radius={[4, 4, 0, 0]}>
                  {weeklyData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.active > 60 ? '#D4FF33' : '#333'} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </div>
          
