import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Plus, Scale, Percent, Activity, Calendar, TrendingUp, TrendingDown, Minus 
} from 'lucide-react';
import { 
  ResponsiveContainer, LineChart, Line, XAxis, Tooltip, YAxis, CartesianGrid 
} from 'recharts';
import BodyStatsModel from '../components/BodyStatsModel';

const BodyStats = () => {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [loading, setLoading] = useState(true);

  // Fetch Stats
  const fetchStats = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/stats', {
        headers: { 'Authorization': `Bearer ${user.token}` }
      });
      const data = await res.json();
      // Sort data by date (newest first for list, oldest first for graph)
      setStats(data.sort((a, b) => new Date(b.date) - new Date(a.date)));
    } catch (err) { console.error(err); } finally { setLoading(false); }
  };

  useEffect(() => { if(user) fetchStats(); }, [user]);

  // Derived Metrics
  const current = stats[0] || {};
  const previous = stats[1] || {};
  
  // Calculate Weight Change
  const weightChange = current.weight && previous.weight ? (current.weight - previous.weight).toFixed(1) : 0;
  const isWeightDown = weightChange < 0;

  // Prepare Graph Data (Oldest -> Newest)
  const graphData = [...stats].reverse().map(s => ({
    date: new Date(s.date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
    weight: s.weight,
    bodyFat: s.bodyFat
  }));

  // Reusable Metric Card
  const MetricCard = ({ label, value, unit, icon: Icon, color, change }) => (
    <div className="bg-[#121212] border border-gray-800 p-5 rounded-2xl relative overflow-hidden flex flex-col justify-between h-32">
      <div className="flex justify-between items-start">
        <div className={`p-2 rounded-lg bg-opacity-10 ${color}`}>
          <Icon size={20} className={color.replace('bg-', 'text-')} />
        </div>
        {change !== undefined && change !== 0 && (
          <div className={`flex items-center text-xs font-bold ${change < 0 ? 'text-[#D4FF33]' : 'text-red-500'}`}>
            {change < 0 ? <TrendingDown size={14} className="mr-1"/> : <TrendingUp size={14} className="mr-1"/>}
            {Math.abs(change)} {unit}
          </div>
        )}
      </div>
      <div>
        <p className="text-xs text-gray-500 uppercase font-bold tracking-wider mb-1">{label}</p>
        <div className="flex items-baseline gap-1">
           <span className="text-3xl font-bold text-white">{value || '--'}</span>
           <span className="text-sm text-gray-400 font-medium">{unit}</span>
        </div>
      </div>
    </div>
  );

  return (
    <div className="p-6 pb-24 min-h-screen text-white">
      
      {/* HEADER */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold mb-1">Body Composition</h1>
          <p className="text-gray-400">Track your weight and biometrics</p>
        </div>
        <button 
          onClick={() => setIsModalOpen(true)}
          className="bg-[#D4FF33] text-black font-bold px-5 py-2.5 rounded-xl flex items-center gap-2 hover:opacity-90 transition-all shadow-lg shadow-[#D4FF33]/20"
        >
          <Plus size={20} /> Log New Entry
        </button>
      </div>

      {/* KEY METRICS GRID */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <MetricCard 
          label="Current Weight" 
          value={current.weight} 
          unit="kg" 
          icon={Scale} 
          color="bg-yellow-500 text-yellow-500"
          change={Number(weightChange)}
        />
        <MetricCard 
          label="Body Fat" 
          value={current.bodyFat} 
          unit="%" 
          icon={Percent} 
          color="bg-blue-500 text-blue-500"
        />
        <MetricCard 
          label="Heart Rate" 
          value={current.heartRate} 
          unit="bpm" 
          icon={Activity} 
          color="bg-red-500 text-red-500"
        />
      </div>

      {/* GRAPH SECTION */}
      <div className="bg-[#121212] border border-gray-800 p-6 rounded-2xl mb-8 h-96 relative">
        <div className="flex items-center justify-between mb-6">
            <div className="flex items-center gap-2">
                <TrendingUp size={20} className="text-[#D4FF33]" />
                <h3 className="font-bold">Weight Progress</h3>
            </div>
            <div className="flex items-center gap-4 text-xs">
                <span className="flex items-center gap-1"><div className="w-2 h-2 rounded-full bg-[#D4FF33]"></div> Weight</span>
            </div>
        </div>

        {graphData.length > 0 ? (
          <ResponsiveContainer width="100%" height="85%">
            <LineChart data={graphData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#222" vertical={false} />
              <XAxis 
                dataKey="date" 
                stroke="#555" 
                fontSize={12} 
                tickLine={false} 
                axisLine={false} 
                dy={10}
              />
              <YAxis 
                stroke="#555" 
                fontSize={12} 
                tickLine={false} 
                axisLine={false} 
                domain={['auto', 'auto']} 
                dx={-10}
              />
              <Tooltip 
                contentStyle={{ backgroundColor: '#1e1e1e', borderColor: '#333', color: '#fff', borderRadius: '8px' }}
                itemStyle={{ color: '#D4FF33' }}
                cursor={{ stroke: '#333', strokeWidth: 2 }}
              />
              <Line 
                type="monotone" 
                dataKey="weight" 
                stroke="#D4FF33" 
                strokeWidth={3} 
                dot={{r: 4, fill:'#0A0A0A', stroke:'#D4FF33', strokeWidth: 2}} 
                activeDot={{r: 6, fill:'#D4FF33'}} 
              />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <div className="h-full flex flex-col items-center justify-center text-gray-500">
             <Scale size={40} className="mb-2 opacity-50"/>
             <p>No data yet. Log your first entry!</p>
          </div>
        )}
      </div>

      {/* HISTORY LIST */}
      <div className="space-y-4">
        <h3 className="font-bold text-lg mb-4">Log History</h3>
        {stats.map((item, index) => (
           <div key={item._id} className="bg-[#121212] border border-gray-800 p-4 rounded-xl flex justify-between items-center text-sm hover:bg-[#1a1a1a] transition-colors group">
             <div className="flex items-center gap-4 text-gray-400">
               <div className="p-2 bg-gray-900 rounded-lg group-hover:bg-[#D4FF33] group-hover:text-black transition-colors">
                  <Calendar size={16} />
               </div>
               <span className="font-medium text-gray-300">{new Date(item.date).toDateString()}</span>
             </div>
             
             <div className="flex gap-8 text-right">
               <div className="flex flex-col md:flex-row md:gap-1 items-end md:items-baseline">
                 <span className="text-white font-bold text-base">{item.weight}</span>
                 <span className="text-gray-600 text-xs">kg</span>
               </div>
               
               {item.bodyFat && (
                 <div className="flex flex-col md:flex-row md:gap-1 items-end md:items-baseline w-16">
                   <span className="text-blue-400 font-bold text-base">{item.bodyFat}</span>
                   <span className="text-gray-600 text-xs">%</span>
                 </div>
               )}
             </div>
           </div>
        ))}
      </div>

      {/* POPUP MODAL */}
      <BodyStatsModel 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        onSave={fetchStats} 
      />
    </div>
  );
};

export default BodyStats;
