import React, { useState, useEffect, useContext } from 'react';
import { Plus, Target, CheckCircle, TrendingUp, Calendar, Trash2 } from 'lucide-react';
import { AuthContext } from '../context/AuthContext';
import { ThemeContext } from '../context/ThemeContext';

const Goals = () => {
  const { user } = useContext(AuthContext);
  const { styles } = useContext(ThemeContext);
  const [goals, setGoals] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchGoals = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/goals', {
        headers: { 'Authorization': `Bearer ${user.token}` }
      });
      const data = await res.json();
      setGoals(data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => { if(user) fetchGoals(); }, [user]);

  // Derived Stats
  const activeGoals = goals.filter(g => g.status === 'active');
  const completedGoals = goals.filter(g => g.status === 'completed');
  const completionRate = goals.length > 0 ? Math.round((completedGoals.length / goals.length) * 100) : 0;

  // Handle Progress Update (e.g., adding +1 to a goal)
  const updateProgress = async (goalId, current, target) => {
    const newProgress = Math.min(current + 1, target); // Increment by 1 for demo
    await fetch(`http://localhost:5000/api/goals/${goalId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${user.token}` },
      body: JSON.stringify({ currentProgress: newProgress })
    });
    fetchGoals();
  };

  return (
    <div className={`min-h-screen ${styles.bg} p-6 pb-24 ${styles.text} transition-colors`}>
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold">Goals</h1>
          <p className={styles.subtext}>Set and track your fitness objectives</p>
        </div>
        <button 
          onClick={() => setIsModalOpen(true)}
          className="bg-primary text-black font-bold px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90"
        >
          <Plus size={20} /> New Goal
        </button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <div className={`${styles.card} border ${styles.border} p-5 rounded-xl`}>
          <p className="text-xs text-gray-500 uppercase font-bold">Active Goals</p>
          <div className="flex items-center gap-2 mt-2 text-primary text-2xl font-bold">
            <Target /> {activeGoals.length}
          </div>
        </div>
        <div className={`${styles.card} border ${styles.border} p-5 rounded-xl`}>
          <p className="text-xs text-gray-500 uppercase font-bold">Completed</p>
          <div className="flex items-center gap-2 mt-2 text-green-500 text-2xl font-bold">
            <CheckCircle /> {completedGoals.length}
          </div>
        </div>
        <div className={`${styles.card} border ${styles.border} p-5 rounded-xl`}>
          <p className="text-xs text-gray-500 uppercase font-bold">Completion Rate</p>
          <div className="flex items-center gap-2 mt-2 text-blue-500 text-2xl font-bold">
            <TrendingUp /> {completionRate}%
          </div>
        </div>
      </div>

      {/* Active Goals List */}
      <h3 className="font-bold mb-4">Active Goals</h3>
      {activeGoals.length === 0 ? (
        <div className={`${styles.card} border ${styles.border} p-12 rounded-xl text-center`}>
          <Target className="mx-auto text-gray-600 mb-3" size={40} />
          <p className="text-gray-500">No active goals. Set one to start tracking!</p>
        </div>
      ) : (
        <div className="space-y-4">
          {activeGoals.map(goal => {
            const pct = Math.min((goal.currentProgress / goal.targetValue) * 100, 100);
            return (
              <div key={goal._id} className={`${styles.card} border ${styles.border} p-5 rounded-xl`}>
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className={`font-bold text-lg ${styles.text}`}>{goal.title}</h3>
                    <p className={`text-xs ${styles.subtext}`}>Deadline: {new Date(goal.deadline).toLocaleDateString()}</p>
                  </div>
                  <span className="text-primary font-bold">{pct.toFixed(0)}%</span>
                </div>
                {/* Progress Bar */}
                <div className="h-3 w-full bg-gray-700 rounded-full overflow-hidden mb-2">
                  <div className="h-full bg-primary transition-all duration-500" style={{ width: `${pct}%` }}></div>
                </div>
                <div className="flex justify-between items-center text-xs text-gray-500 mt-2">
                  <span>{goal.currentProgress} / {goal.targetValue} {goal.unit}</span>
                  <button 
                    onClick={() => updateProgress(goal._id, goal.currentProgress, goal.targetValue)}
                    className="text-primary hover:underline font-bold"
                  >
                    + Update Progress
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* --- CREATE GOAL MODAL --- */}
      {isModalOpen && (
        <GoalModal 
          isOpen={isModalOpen} 
          onClose={() => setIsModalOpen(false)} 
          onSave={fetchGoals} 
          styles={styles} 
        />
      )}
    </div>
  );
};

// Internal Modal Component
const GoalModal = ({ isOpen, onClose, onSave, styles }) => {
  const { user } = useContext(AuthContext);
  const [formData, setFormData] = useState({ title: '', targetValue: '', unit: '', deadline: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await fetch('http://localhost:5000/api/goals', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${user.token}` },
        body: JSON.stringify(formData)
    });
    onSave(); onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className={`${styles.card} border ${styles.border} w-full max-w-lg rounded-2xl p-6 shadow-2xl`}>
        <h2 className={`text-xl font-bold mb-4 ${styles.text}`}>Create New Goal</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
             <label className={`text-xs ${styles.subtext} mb-1 block`}>Goal Title</label>
             <input type="text" placeholder="e.g. Lose 5kg" className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text}`} value={formData.title} onChange={e=>setFormData({...formData, title: e.target.value})} />
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className={`text-xs ${styles.subtext} mb-1 block`}>Target Value</label>
              <input type="number" className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text}`} value={formData.targetValue} onChange={e=>setFormData({...formData, targetValue: e.target.value})} />
            </div>
            <div>
              <label className={`text-xs ${styles.subtext} mb-1 block`}>Unit</label>
              <input type="text" placeholder="kg, steps" className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text}`} value={formData.unit} onChange={e=>setFormData({...formData, unit: e.target.value})} />
            </div>
          </div>
          <div>
             <label className={`text-xs ${styles.subtext} mb-1 block`}>Deadline</label>
             <input type="date" className={`w-full ${styles.input} border ${styles.border} rounded-lg p-3 ${styles.text}`} value={formData.deadline} onChange={e=>setFormData({...formData, deadline: e.target.value})} />
          </div>
          <button type="submit" className="w-full bg-primary text-black font-bold py-3 rounded-xl mt-4">Create Goal</button>
          <button type="button" onClick={onClose} className="w-full mt-2 text-sm text-gray-500 hover:text-white">Cancel</button>
        </form>
      </div>
    </div>
  );
};

export default Goals;
