import React, { useState, useEffect, useContext } from 'react';
import { Plus, Target, CheckCircle, TrendingUp, Calendar } from 'lucide-react'; // Added Calendar, Removed Trash2
import { AuthContext } from '../context/AuthContext';

const Goals = () => {
  const { user } = useContext(AuthContext);
  const [goals, setGoals] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/goals', {
          headers: { 'Authorization': `Bearer ${user.token}` }
        });
        const data = await res.json();
        setGoals(data);
      } catch (err) { console.error(err); }
    };
    if(user) fetchGoals();
  }, [user]);

  const activeGoals = goals.filter(g => g.status === 'active');
  const completedGoals = goals.filter(g => g.status === 'completed');
  const completionRate = goals.length > 0 ? Math.round((completedGoals.length / goals.length) * 100) : 0;

  const updateProgress = async (goalId, current, target) => {
    const newProgress = Math.min(current + 1, target); 
    await fetch(`http://localhost:5000/api/goals/${goalId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${user.token}` },
      body: JSON.stringify({ currentProgress: newProgress })
    });
    window.location.reload(); // Simple reload to refresh
  };

  return (
    <div className="min-h-screen bg-[#0A0A0A] p-6 pb-24 text-white">
      <div className="flex justify-between items-center mb-8">
        <div><h1 className="text-3xl font-bold">Goals</h1><p className="text-gray-400">Set and track objectives</p></div>
        <button onClick={() => setIsModalOpen(true)} className="bg-[#D4FF33] text-black font-bold px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90"><Plus size={20} /> New Goal</button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-xl"><p className="text-xs text-gray-500 uppercase font-bold">Active Goals</p><div className="flex items-center gap-2 mt-2 text-[#D4FF33] text-2xl font-bold"><Target /> {activeGoals.length}</div></div>
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-xl"><p className="text-xs text-gray-500 uppercase font-bold">Completed</p><div className="flex items-center gap-2 mt-2 text-green-500 text-2xl font-bold"><CheckCircle /> {completedGoals.length}</div></div>
        <div className="bg-[#121212] border border-gray-800 p-5 rounded-xl"><p className="text-xs text-gray-500 uppercase font-bold">Completion Rate</p><div className="flex items-center gap-2 mt-2 text-blue-500 text-2xl font-bold"><TrendingUp /> {completionRate}%</div></div>
      </div>

      <h3 className="font-bold mb-4">Active Goals</h3>
      {activeGoals.map(goal => {
        const pct = Math.min((goal.currentProgress / goal.targetValue) * 100, 100);
        return (
          <div key={goal._id} className="bg-[#121212] border border-gray-800 p-5 rounded-xl mb-4">
            <div className="flex justify-between items-start mb-4">
              <div><h3 className="font-bold text-lg text-white">{goal.title}</h3><p className="text-xs text-gray-500">Deadline: {new Date(goal.deadline).toLocaleDateString()}</p></div>
              <span className="text-[#D4FF33] font-bold">{pct.toFixed(0)}%</span>
            </div>
            <div className="h-3 w-full bg-gray-700 rounded-full overflow-hidden mb-2"><div className="h-full bg-[#D4FF33]" style={{ width: `${pct}%` }}></div></div>
            <div className="flex justify-between items-center text-xs text-gray-500 mt-2">
              <span>{goal.currentProgress} / {goal.targetValue} {goal.unit}</span>
              <button onClick={() => updateProgress(goal._id, goal.currentProgress, goal.targetValue)} className="text-[#D4FF33] hover:underline font-bold">+ Update Progress</button>
            </div>
          </div>
        );
      })}
      
      {isModalOpen && (
        <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center z-50 p-4">
           {/* Minimal Modal Content for brevity - you can copy full modal from previous code if needed */}
           <div className="bg-[#121212] p-6 rounded-2xl border border-gray-800 w-full max-w-lg">
              <h2 className="text-white font-bold text-xl mb-4">Create Goal</h2>
              <p className="text-gray-400 mb-4">Goal creation form goes here (refer to previous code)...</p>
              <button onClick={() => setIsModalOpen(false)} className="text-white underline">Close (Placeholder)</button>
           </div>
        </div>
      )}
    </div>
  );
};

export default Goals;
