import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { X, Scale, Footprints, Moon, Droplets, Dumbbell, Ruler } from 'lucide-react';

const BodyStatsModel = ({ isOpen, onClose, onSave }) => {
  const { user } = useContext(AuthContext);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    weight: '',      // Required by your Schema
    bodyFat: '',
    muscleMass: '',
    steps: '',
    sleep: '',
    water: ''
  });

  if (!isOpen) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch('http://localhost:5000/api/stats', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(formData)
      });
      
      if (res.ok) {
        onSave(); // Refresh dashboard data
        onClose();
        // Reset form
        setFormData({ weight: '', bodyFat: '', muscleMass: '', steps: '', sleep: '', water: '' });
      } else {
        alert("Failed to save stats. Make sure Weight is filled.");
      }
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-[#121212] border border-white/10 w-full max-w-md rounded-2xl p-6 relative">
        <button onClick={onClose} className="absolute right-4 top-4 text-gray-400 hover:text-white"><X size={20} /></button>
        
        <h2 className="text-xl font-bold mb-6 flex items-center gap-2">
          <Scale className="text-blue-500" /> Log Daily Stats
        </h2>

        <form onSubmit={handleSubmit} className="space-y-4">
          
          {/* PRIMARY STATS */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="text-xs text-gray-500 font-bold mb-1 block">Weight (kg) *</label>
              <div className="relative">
                <Scale className="absolute left-3 top-3 text-gray-500" size={16} />
                <input 
                  type="number" step="0.1" required 
                  className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-2.5 pl-10 text-white focus:border-blue-500 outline-none" 
                  placeholder="70" 
                  value={formData.weight} 
                  onChange={e => setFormData({...formData, weight: e.target.value})} 
                />
              </div>
            </div>
             <div>
              <label className="text-xs text-gray-500 font-bold mb-1 block">Muscle (kg)</label>
              <div className="relative">
                <Dumbbell className="absolute left-3 top-3 text-gray-500" size={16} />
                <input 
                  type="number" step="0.1" 
                  className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-2.5 pl-10 text-white focus:border-purple-500 outline-none" 
                  placeholder="30" 
                  value={formData.muscleMass} 
                  onChange={e => setFormData({...formData, muscleMass: e.target.value})} 
                />
              </div>
            </div>
          </div>

          {/* DAILY TRACKING */}
          <div className="grid grid-cols-3 gap-3">
             {/* STEPS */}
             <div>
              <label className="text-xs text-gray-500 font-bold mb-1 block">Steps</label>
              <div className="relative">
                <Footprints className="absolute left-3 top-3 text-gray-500" size={14} />
                <input type="number" className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-2.5 pl-9 text-white focus:border-green-500 outline-none text-sm" placeholder="5000" 
                  value={formData.steps} onChange={e => setFormData({...formData, steps: e.target.value})} />
              </div>
            </div>
             {/* SLEEP */}
             <div>
              <label className="text-xs text-gray-500 font-bold mb-1 block">Sleep (h)</label>
              <div className="relative">
                <Moon className="absolute left-3 top-3 text-gray-500" size={14} />
                <input type="number" step="0.1" className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-2.5 pl-9 text-white focus:border-indigo-500 outline-none text-sm" placeholder="7.5" 
                  value={formData.sleep} onChange={e => setFormData({...formData, sleep: e.target.value})} />
              </div>
            </div>
             {/* WATER */}
             <div>
              <label className="text-xs text-gray-500 font-bold mb-1 block">Water (L)</label>
              <div className="relative">
                <Droplets className="absolute left-3 top-3 text-gray-500" size={14} />
                <input type="number" step="0.1" className="w-full bg-[#1e1e1e] border border-gray-700 rounded-xl p-2.5 pl-9 text-white focus:border-cyan-500 outline-none text-sm" placeholder="2.5" 
                  value={formData.water} onChange={e => setFormData({...formData, water: e.target.value})} />
              </div>
            </div>
          </div>

          <button disabled={loading} className="w-full bg-blue-600 hover:bg-blue-500 text-white font-bold py-3 rounded-xl mt-2">
            {loading ? 'Saving...' : 'Save Stats'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BodyStatsModel;
