import React, { useState, useContext, useRef, useEffect } from 'react';
import { Send, Bot, User, Zap, Sparkles, ChevronRight, Activity } from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

const AICoach = () => {
  const { user } = useContext(AuthContext);
  const [messages, setMessages] = useState([
    { 
      role: 'ai', 
      content: `Hello ${user?.name || 'Champ'}! üëã\nI am your AI Performance Coach.\n\nI can build **Workout Plans**, suggest **Diet Macros**, or help with **Injury Recovery**.\n\nWhat is your goal for today?` 
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const handleSend = async (text = input) => {
    if (!text.trim()) return;

    const userMsg = { role: 'user', content: text };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ message: text })
      });
      
      const data = await response.json();
      const aiMsg = { role: 'ai', content: data.response };
      setMessages(prev => [...prev, aiMsg]);

    } catch (error) {
      setMessages(prev => [...prev, { role: 'ai', content: "‚ö†Ô∏è Connection Error: Server is unreachable." }]);
    } finally {
      setLoading(false);
    }
  };

  // --- SMART TEXT FORMATTER (Bold & Lists support) ---
  const FormatMessage = ({ content }) => {
    return content.split('\n').map((line, i) => {
      // Bold Text Handling (**text**)
      const parts = line.split(/(\*\*.*?\*\*)/g);
      
      return (
        <div key={i} className={`mb-1 ${line.trim().startsWith('-') || line.trim().match(/^\d+\./) ? 'pl-4' : ''}`}>
          {parts.map((part, index) => {
            if (part.startsWith('**') && part.endsWith('**')) {
              return <strong key={index} className="text-[#D4FF33] font-bold">{part.slice(2, -2)}</strong>;
            }
            return <span key={index}>{part}</span>;
          })}
        </div>
      );
    });
  };

  const suggestions = [
    "üèãÔ∏è Generate Chest Workout",
    "ü•ó Vegetarian Diet Plan",
    "ü¶µ Leg Day Routine",
    "üç´ Abs Exercises"
  ];

  return (
    <div className="flex flex-col h-screen bg-[#050505] text-white font-sans relative overflow-hidden">
      
      {/* Background Gradient Blob */}
      <div className="absolute top-[-10%] left-[-10%] w-[500px] h-[500px] bg-[#D4FF33] rounded-full mix-blend-screen filter blur-[120px] opacity-10 pointer-events-none"></div>

      {/* --- HEADER --- */}
      <div className="flex items-center justify-between p-6 backdrop-blur-md bg-black/30 border-b border-white/5 sticky top-0 z-10">
        <div className="flex items-center gap-4">
          <div className="relative">
            <div className="w-12 h-12 rounded-xl bg-gradient-to-tr from-[#D4FF33] to-[#a3cc00] flex items-center justify-center shadow-lg shadow-[#D4FF33]/20">
              <Bot size={24} className="text-black" />
            </div>
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 border-2 border-black rounded-full animate-pulse"></div>
          </div>
          <div>
            <h1 className="text-xl font-bold tracking-wide">AI Coach</h1>
            <div className="flex items-center gap-2 text-xs text-gray-400">
              <Activity size={12} className="text-[#D4FF33]" />
              <span>Online & Ready</span>
            </div>
          </div>
        </div>
      </div>

      {/* --- CHAT AREA --- */}
      <div className="flex-1 overflow-y-auto p-4 sm:p-6 space-y-6 custom-scrollbar">
        {messages.map((msg, index) => (
          <div 
            key={index} 
            className={`flex gap-4 ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-fade-in-up`}
          >
            {/* AI Avatar */}
            {msg.role === 'ai' && (
              <div className="w-8 h-8 rounded-full bg-[#1e1e1e] border border-gray-700 flex items-center justify-center flex-shrink-0 mt-2">
                 <Sparkles size={14} className="text-[#D4FF33]" />
              </div>
            )}

            {/* Message Bubble */}
            <div className={`p-4 rounded-2xl max-w-[85%] sm:max-w-[75%] shadow-md text-[15px] leading-relaxed tracking-wide ${
              msg.role === 'user' 
                ? 'bg-[#D4FF33] text-black font-medium rounded-tr-none shadow-[#D4FF33]/10' 
                : 'bg-[#181818] text-gray-200 border border-white/10 rounded-tl-none'
            }`}>
              <FormatMessage content={msg.content} />
            </div>

            {/* User Avatar */}
            {msg.role === 'user' && (
               <div className="w-8 h-8 rounded-full bg-gray-800 border border-gray-700 flex items-center justify-center flex-shrink-0 mt-2">
                  <User size={14} className="text-gray-300" />
               </div>
            )}
          </div>
        ))}
        
        {/* Loading Indicator */}
        {loading && (
          <div className="flex gap-4">
             <div className="w-8 h-8 rounded-full bg-[#1e1e1e] border border-gray-700 flex items-center justify-center flex-shrink-0">
                 <Sparkles size={14} className="text-[#D4FF33]" />
              </div>
              <div className="bg-[#181818] p-4 rounded-2xl rounded-tl-none border border-white/10 flex items-center gap-2">
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce delay-100"></div>
                <div className="w-2 h-2 bg-[#D4FF33] rounded-full animate-bounce delay-200"></div>
              </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* --- SUGGESTIONS & INPUT --- */}
      <div className="p-4 bg-black/80 backdrop-blur-xl border-t border-white/5">
        
        {/* Quick Chips */}
        {messages.length < 3 && (
          <div className="flex gap-2 overflow-x-auto pb-3 mb-2 no-scrollbar">
            {suggestions.map((text, idx) => (
              <button 
                key={idx}
                onClick={() => handleSend(text)}
                className="bg-[#1A1A1A] border border-white/10 text-gray-300 px-4 py-2 rounded-full text-xs hover:bg-[#D4FF33] hover:text-black hover:border-[#D4FF33] transition-all whitespace-nowrap"
              >
                {text}
              </button>
            ))}
          </div>
        )}

        {/* Input Bar */}
        <div className="relative max-w-4xl mx-auto flex items-center gap-2">
          <input 
            type="text" 
            className="w-full bg-[#121212] border border-white/10 rounded-2xl p-4 pl-5 text-white placeholder-gray-500 focus:border-[#D4FF33] focus:ring-1 focus:ring-[#D4FF33] focus:outline-none transition-all shadow-inner"
            placeholder="Ask AI for workouts, diets, or tips..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button 
            onClick={() => handleSend()}
            disabled={loading || !input.trim()}
            className="p-4 bg-[#D4FF33] rounded-2xl text-black hover:bg-[#b8e600] disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105 transition-all shadow-lg shadow-[#D4FF33]/20"
          >
            <Send size={20} />
          </button>
        </div>
        <p className="text-center text-[10px] text-gray-600 mt-2">AI can make mistakes. Verify important health info.</p>
      </div>

      {/* CSS Animation for Chat Bubbles */}
      <style jsx>{`
        @keyframes fadeInUp {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in-up {
          animation: fadeInUp 0.3s ease-out forwards;
        }
        .no-scrollbar::-webkit-scrollbar {
          display: none;
        }
        .no-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
};

export default AICoach;
