import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { 
  Activity, Flame, Footprints, Moon, Droplets, 
  Dumbbell, TrendingUp, Calendar, Zap
} from 'lucide-react';
import { 
  BarChart, Bar, XAxis, Tooltip, ResponsiveContainer, Cell 
} from 'recharts';

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  
  // --- 1. WORKOUT SCHEDULE LOGIC ---
  const getDailyFocus = () => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const today = days[new Date().getDay()];
    
    // You can customize this schedule!
    const schedule = {
      'Monday': { focus: 'Chest & Triceps', icon: 'ðŸ’ª' },
      'Tuesday': { focus: 'Back & Biceps', icon: 'ðŸ‹ï¸' },
      'Wednesday': { focus: 'Legs & Core', icon: 'ðŸ¦µ' },
      'Thursday': { focus: 'Shoulders & Abs', icon: 'ðŸ§˜' },
      'Friday': { focus: 'Full Body HIIT', icon: 'ðŸ”¥' },
      'Saturday': { focus: 'Yoga & Recovery', icon: 'ðŸ§˜' },
      'Sunday': { focus: 'Rest Day', icon: 'ðŸ˜´' }
    };

    return { day: today, ...schedule[today] };
  };

  const dailySuggestion = getDailyFocus();

  // --- 2. STATE FOR REAL DATA ---
  const [stats, setStats] = useState({
    calories: 0,
    protein: 0,
    carbs: 0,
    fat: 0,
    steps: 0,
    workouts: 0,
    activeMinutes: 0
  });

  // --- 3. FETCH DATA ---
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = user?.token;
        if (!token) return;
        
        const headers = { 'Authorization': `Bearer ${token}` };

        // Fetch all necessary data
        const [workoutsRes, nutritionRes, statsRes] = await Promise.all([
          fetch('http://localhost:5000/api/workouts', { headers }),
          fetch('http://localhost:5000/api/nutrition', { headers }),
          fetch('http://localhost:5000/api/stats', { headers })
        ]);

        const workoutsData = await workoutsRes.json();
        const nutritionData = await nutritionRes.json();
        const statsData = await statsRes.json();

        // Calculate Totals for TODAY
        const todayStr = new Date().toDateString();

        let cal = 0, prot = 0, carb = 0, fat = 0;
        nutritionData.forEach(item => {
          if (new Date(item.date).toDateString() === todayStr) {
            cal += item.calories || 0;
            prot += item.protein || 0;
            carb += item.carbs || 0;
            fat += item.fat || 0;
          }
        });

        let mins = 0, count = 0;
        workoutsData.forEach(item => {
          if (new Date(item.date).toDateString() === todayStr) {
            count += 1;
            mins += item.duration || 0;
          }
        });

        const latestStat = statsData[0] || {}; 
        const steps = latestStat.steps || 0;

        setStats({
          calories: cal,
          protein: prot,
          carbs: carb,
          fat: fat,
          steps: steps,
          workouts: count,
          activeMinutes: mins
        });

      } catch (error) {
        console.error("Error loading dashboard data:", error);
      }
    };

    if (user) fetchData();
  }, [user]);

  // --- REUSABLE CARD COMPONENTS ---
  const SummaryCard = ({ icon: Icon, label, value, subtext, color }) => (
    <div className="bg-card border border-gray-800 p-4 rounded-xl flex items-center gap-4">
      <div className={`p-3 rounded-lg ${color} bg-opacity-10 text-white`}>
        <Icon size={24} className={color.replace('bg-', 'text-')} />
      </div>
      <div>
        <p className="text-gray-400 text-xs uppercase font-bold tracking-wider">{label}</p>
        <div className="flex items-baseline gap-1">
           <h3 className="text-xl font-bold text-white">{value}</h3>
           <span className="text-xs text-gray-500">{subtext}</span>
        </div>
      </div>
    </div>
  );

  const MetricCard = ({ title, value, goal, unit, icon: Icon, progressColor, details }) => {
    const percentage = Math.min((value / goal) * 100, 100);
    return (
      <div className="bg-card border border-gray-800 p-6 rounded-2xl relative overflow-hidden">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h3 className="text-gray-400 font-medium text-sm flex items-center gap-2">
              <Icon size={16} /> {title}
            </h3>
            <div className="mt-1">
              <span className="text-3xl font-bold text-white">{value}</span>
              <span className="text-gray-500 text-sm ml-1">/ {goal} {unit}</span>
            </div>
          </div>
        </div>
        <div className="h-2 w-full bg-gray-800 rounded-full mb-4">
          <div className={`h-full rounded-full ${progressColor}`} style={{ width: `${percentage}%` }}></div>
        </div>
        <div className="flex justify-between text-xs text-gray-400 border-t border-gray-800 pt-3">
          {details.map((detail, idx) => (
            <div key={idx}>
              <span className="block text-white font-semibold">{detail.val}</span>
              <span>{detail.label}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-background p-6 pb-24 text-white">
      {/* Header Section */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-end mb-8 gap-4">
        <div>
          <h1 className="text-3xl font-bold">Welcome back, {user?.name}!</h1>
          <p className="text-gray-400">Here's your fitness breakdown.</p>
        </div>

        {/* --- NEW: DAILY SUGGESTION CARD --- */}
        <div className="bg-gradient-to-r from-primary to-green-400 text-black px-6 py-3 rounded-xl flex items-center gap-4 shadow-lg shadow-primary/20">
          <div className="bg-black/20 p-2 rounded-full">
            <Calendar size={24} className="text-black" />
          </div>
          <div>
            <p className="text-xs font-bold opacity-80 uppercase tracking-wider">{dailySuggestion.day}'s Focus</p>
            <h3 className="text-lg font-bold flex items-center gap-2">
              {dailySuggestion.icon} {dailySuggestion.focus}
            </h3>
          </div>
          <button className="bg-black text-white px-3 py-1 rounded-lg text-xs font-bold ml-4">
            Start
          </button>
        </div>
      </div>

      {/* Quick Stats Row */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <SummaryCard icon={Flame} label="Calories" value={stats.calories} subtext="/ 2,500" color="bg-orange-500 text-orange-500" />
        <SummaryCard icon={Footprints} label="Steps" value={stats.steps} subtext="/ 10k" color="bg-green-500 text-green-500" />
        <SummaryCard icon={Activity} label="Active Mins" value={stats.activeMinutes} subtext="/ 60" color="bg-pink-500 text-pink-500" />
        <SummaryCard icon={Dumbbell} label="Workouts" value={stats.workouts} subtext="sessions" color="bg-indigo-500 text-indigo-500" />
      </div>

      {/* Main Grid Layout */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        
        {/* Left Column: Detailed Cards */}
        <div className="md:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-6">
          <MetricCard 
            title="Steps" 
            value={stats.steps} 
            goal={10000} 
            unit="steps" 
            icon={Footprints} 
            progressColor="bg-green-500"
            details={[{val: `${(stats.steps * 0.0008).toFixed(1)}km`, label: 'Distance'}, {val: `${(stats.steps * 0.04).toFixed(0)}`, label: 'Cal Burned'}]} 
          />
          <MetricCard 
            title="Nutrition" 
            value={stats.calories} 
            goal={2500} 
            unit="cal" 
            icon={Flame} 
            progressColor="bg-blue-500"
            details={[{val: `${stats.protein}g`, label: 'Protein'}, {val: `${stats.carbs}g`, label: 'Carbs'}, {val: `${stats.fat}g`, label: 'Fat'}]} 
          />
        </div>

        {/* Right Column: Placeholder Graph */}
        <div className="bg-card border border-gray-800 p-6 rounded-2xl flex flex-col justify-center items-center text-center">
          <div className="p-4 bg-gray-800 rounded-full mb-4">
            <Zap size={32} className="text-primary" />
          </div>
          <h3 className="text-lg font-bold mb-2">Weekly Trends</h3>
          <p className="text-gray-400 text-sm">Log workouts for 7 days to unlock your weekly analysis graph.</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
