import React, { useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { LayoutDashboard, Dumbbell, Apple, Bot, User } from 'lucide-react';
import { ThemeContext } from '../context/ThemeContext'; // Import Theme

const BottomNav = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { styles, theme } = useContext(ThemeContext); // Get styles

  const navItems = [
    { name: 'Home', icon: LayoutDashboard, path: '/dashboard' },
    { name: 'Workouts', icon: Dumbbell, path: '/workouts' },
    { name: 'AI Coach', icon: Bot, path: '/ai-coach' },
    { name: 'Nutrition', icon: Apple, path: '/nutrition' },
    { name: 'Profile', icon: User, path: '/profile' },
  ];

  return (
    <div className={`fixed bottom-0 left-0 right-0 ${styles.card} border-t ${styles.border} px-6 py-3 md:hidden z-50 transition-colors duration-300`}>
      <div className="flex justify-between items-center">
        {navItems.map((item) => {
          const isActive = location.pathname === item.path;
          return (
            <button
              key={item.name}
              onClick={() => navigate(item.path)}
              className={`flex flex-col items-center gap-1 transition-colors ${
                isActive 
                  ? 'text-primary' 
                  : theme === 'light' ? 'text-gray-400' : 'text-gray-500'
              }`}
            >
              <item.icon size={24} />
              <span className="text-[10px] font-medium">{item.name}</span>
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default BottomNav;
