import React, { useState, useContext, useRef, useEffect } from 'react';
import { Send, Bot, User, Sparkles, Activity } from 'lucide-react';
import { AuthContext } from '../context/AuthContext';

const AICoach = () => {
  const { user } = useContext(AuthContext);
  const [messages, setMessages] = useState([
    { 
      role: 'ai', 
      content: `Hello ${user?.name || 'Champ'}! üëã\nI am your AI Coach.\nWhat is your goal for today?` 
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const handleSend = async (text = input) => {
    if (!text.trim()) return;

    const userMsg = { role: 'user', content: text };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ message: text })
      });
      
      const data = await response.json();
      const aiMsg = { role: 'ai', content: data.response };
      setMessages(prev => [...prev, aiMsg]);

    } catch (error) {
      setMessages(prev => [...prev, { role: 'ai', content: "‚ö†Ô∏è Connection Error." }]);
    } finally {
      setLoading(false);
    }
  };

  const FormatMessage = ({ content }) => {
    return content.split('\n').map((line, i) => {
      const parts = line.split(/(\*\*.*?\*\*)/g);
      return (
        <div key={i} className={`mb-1 ${line.trim().startsWith('-') ? 'pl-4' : ''}`}>
          {parts.map((part, index) => {
            if (part.startsWith('**') && part.endsWith('**')) {
              return <strong key={index} className="text-[#D4FF33] font-bold">{part.slice(2, -2)}</strong>;
            }
            return <span key={index}>{part}</span>;
          })}
        </div>
      );
    });
  };

  const suggestions = ["üèãÔ∏è Chest Workout", "ü•ó Diet Plan", "ü¶µ Leg Day", "üç´ Abs"];

  return (
    // üëá FIX 1: Height ko 100vh se kam kiya taaki Navbar ke liye jagah bache
    <div className="flex flex-col h-[calc(100vh-85px)] bg-[#050505] text-white font-sans relative overflow-hidden">
      
      {/* Background Glow */}
      <div className="absolute top-[-10%] left-[-10%] w-[300px] h-[300px] bg-[#D4FF33] rounded-full mix-blend-screen filter blur-[100px] opacity-10 pointer-events-none"></div>

      {/* --- HEADER (Compact) --- */}
      <div className="flex items-center justify-between px-4 py-3 backdrop-blur-md bg-black/30 border-b border-white/5 sticky top-0 z-10">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-xl bg-gradient-to-tr from-[#D4FF33] to-[#a3cc00] flex items-center justify-center shadow-lg shadow-[#D4FF33]/20">
            <Bot size={20} className="text-black" />
          </div>
          <div>
            <h1 className="text-lg font-bold">AI Coach</h1>
            <div className="flex items-center gap-1 text-[10px] text-gray-400">
              <Activity size={10} className="text-[#D4FF33]" />
              <span>Online</span>
            </div>
          </div>
        </div>
      </div>

      {/* --- CHAT AREA --- */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4 custom-scrollbar">
        {messages.map((msg, index) => (
          <div key={index} className={`flex gap-3 ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            {msg.role === 'ai' && (
              <div className="w-6 h-6 rounded-full bg-[#1e1e1e] border border-gray-700 flex items-center justify-center flex-shrink-0 mt-2">
                 <Sparkles size={12} className="text-[#D4FF33]" />
              </div>
            )}

            <div className={`p-3 rounded-2xl max-w-[80%] text-sm leading-relaxed ${
              msg.role === 'user' 
                ? 'bg-[#D4FF33] text-black font-medium rounded-tr-none' 
                : 'bg-[#181818] text-gray-200 border border-white/10 rounded-tl-none'
            }`}>
              <FormatMessage content={msg.content} />
            </div>
          </div>
        ))}
        
        {loading && (
          <div className="flex gap-2 ml-9">
              <div className="bg-[#181818] px-4 py-2 rounded-2xl rounded-tl-none border border-white/10 flex items-center gap-1">
                <div className="w-1.5 h-1.5 bg-[#D4FF33] rounded-full animate-bounce"></div>
                <div className="w-1.5 h-1.5 bg-[#D4FF33] rounded-full animate-bounce delay-100"></div>
                <div className="w-1.5 h-1.5 bg-[#D4FF33] rounded-full animate-bounce delay-200"></div>
              </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* --- INPUT AREA (Compact & Lifted) --- */}
      <div className="p-3 bg-black/80 backdrop-blur-xl border-t border-white/5">
        
        {/* Chips */}
        {messages.length < 3 && (
          <div className="flex gap-2 overflow-x-auto pb-2 mb-1 no-scrollbar">
            {suggestions.map((text, idx) => (
              <button key={idx} onClick={() => handleSend(text)} className="bg-[#1A1A1A] border border-white/10 text-gray-300 px-3 py-1.5 rounded-full text-[10px] hover:bg-[#D4FF33] hover:text-black whitespace-nowrap transition-all">
                {text}
              </button>
            ))}
          </div>
        )}

        {/* Input Field */}
        <div className="relative flex items-center gap-2">
          <input 
            type="text" 
            className="w-full bg-[#121212] border border-white/10 rounded-xl p-3 pl-4 text-sm text-white placeholder-gray-500 focus:border-[#D4FF33] focus:outline-none transition-all"
            placeholder="Ask AI..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button 
            onClick={() => handleSend()}
            disabled={loading || !input.trim()}
            className="p-3 bg-[#D4FF33] rounded-xl text-black hover:scale-105 transition-all"
          >
            <Send size={18} />
          </button>
        </div>
      </div>
      
      <style jsx>{`
        .no-scrollbar::-webkit-scrollbar { display: none; }
        .no-scrollbar { -ms-overflow-style: none; scrollbar-width: none; }
      `}</style>
    </div>
  );
};

export default AICoach;
